FROM (SELECT ANY_VALUE("t2"."Id") AS "POSTID", "t2"."Title" AS "TITLE", "t2"."CreationDate" AS "CREATIONDATE", "t2"."Score" AS "SCORE", "t2"."ViewCount" AS "VIEWCOUNT", ANY_VALUE("t2"."DisplayName") AS "OWNERNAME", COUNT("t2"."Id1") AS "COMMENTCOUNT", SUM(CASE WHEN CAST("t2"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("t2"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES", COUNT(DISTINCT "PostHistory"."Id") AS "EDITCOUNT", MIN("PostHistory"."CreationDate") AS "FIRSTEDITDATE", MAX("PostHistory"."CreationDate") AS "LASTEDITDATE"
FROM "STACK"."PostHistory"
RIGHT JOIN (SELECT "s1"."Id", "s1"."PostTypeId", "s1"."AcceptedAnswerId", "s1"."ParentId", "s1"."CreationDate", "s1"."Score", "s1"."ViewCount", "s1"."Body", "s1"."OwnerUserId", "s1"."OwnerDisplayName", "s1"."LastEditorUserId", "s1"."LastEditorDisplayName", "s1"."LastEditDate", "s1"."LastActivityDate", "s1"."Title", "s1"."Tags", "s1"."AnswerCount", "s1"."CommentCount", "s1"."FavoriteCount", "s1"."ClosedDate", "s1"."CommunityOwnedDate", "s1"."ContentLicense", "s1"."Id0", "s1"."Reputation", "s1"."CreationDate0", "s1"."DisplayName", "s1"."LastAccessDate", "s1"."WebsiteUrl", "s1"."Location", "s1"."AboutMe", "s1"."Views", "s1"."UpVotes", "s1"."DownVotes", "s1"."ProfileImageUrl", "s1"."AccountId", "s1"."Id1", "s1"."PostId", "s1"."Score0", "s1"."Text", "s1"."CreationDate1", "s1"."UserDisplayName", "s1"."UserId", "s1"."ContentLicense0", "Votes"."Id" AS "Id2", "Votes"."PostId" AS "PostId0", "Votes"."VoteTypeId", "Votes"."UserId" AS "UserId0", "Votes"."CreationDate" AS "CreationDate2", "Votes"."BountyAmount"
FROM "STACK"."Votes"
