SELECT COALESCE("t6"."USERID", "t6"."USERID") AS "USERID", "t6"."DISPLAYNAME", "t6"."POSTCOUNT", "t6"."QUESTIONCOUNT", "t6"."ANSWERCOUNT", "t6"."POSITIVESCOREPOSTS", "t6"."AVGVIEWCOUNT", "t6"."AVGSCORE", "t6"."EDITCOUNT", "t6"."POSTSEDITED"
FROM (SELECT "t4"."USERID", "t4"."DISPLAYNAME", "t4"."POSTCOUNT", "t4"."QUESTIONCOUNT", "t4"."ANSWERCOUNT", "t4"."POSITIVESCOREPOSTS", "t4"."AVGVIEWCOUNT", "t4"."AVGSCORE", CASE WHEN "s1"."EDITCOUNT" IS NOT NULL THEN CAST("s1"."EDITCOUNT" AS BIGINT) ELSE 0 END AS "EDITCOUNT", CASE WHEN "s1"."POSTSEDITED" IS NOT NULL THEN CAST("s1"."POSTSEDITED" AS BIGINT) ELSE 0 END AS "POSTSEDITED"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Posts"."Id") AS "POSTCOUNT", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "QUESTIONCOUNT", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "ANSWERCOUNT", SUM(CASE WHEN "Posts"."Score" > 0 THEN 1 ELSE 0 END) AS "POSITIVESCOREPOSTS", AVG("Posts"."ViewCount") AS "AVGVIEWCOUNT", AVG("Posts"."Score") AS "AVGSCORE"
FROM "STACK"."Posts"
RIGHT JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id"
GROUP BY "Users"."Id", "Users"."DisplayName") AS "t4"
LEFT JOIN "s1" ON "t4"."USERID" = "s1"."UserId"
ORDER BY "t4"."POSTCOUNT" DESC NULLS FIRST) AS "t6"