SELECT COALESCE("USERID", "USERID") AS "USERID", "REPUTATION", "TOTALPOSTS", "QUESTIONS", "ANSWERS", "TOTALSCORE", "TOTALVIEWS", "AVGVIEWSPERPOST", "TOTALCOMMENTS"
FROM (SELECT "Id", "REPUTATION", ANY_VALUE("Id") AS "USERID", COUNT("Id0") AS "TOTALPOSTS", COUNT("FD_COL_3") AS "QUESTIONS", COUNT("FD_COL_4") AS "ANSWERS", SUM("Score") AS "TOTALSCORE", SUM("FD_COL_6") AS "TOTALVIEWS", AVG("ViewCount") AS "AVGVIEWSPERPOST", COUNT("FD_COL_8") AS "TOTALCOMMENTS"
FROM "s1"
GROUP BY "Id", "REPUTATION"
ORDER BY "REPUTATION" DESC NULLS FIRST, 4 DESC NULLS FIRST) AS "t3"