SELECT COALESCE("t6"."POSTID", "t6"."POSTID") AS "POSTID", "t6"."TITLE", "t6"."CREATIONDATE", "t6"."VIEWCOUNT", "t6"."SCORE", "t6"."OWNERDISPLAYNAME", "t6"."COMMENTCOUNT", "t6"."ANSWERCOUNT", "t6"."VOTECOUNT"
FROM (SELECT ANY_VALUE("t2"."Id") AS "POSTID", "t2"."Title" AS "TITLE", "t2"."CreationDate" AS "CREATIONDATE", "t2"."ViewCount" AS "VIEWCOUNT", "t2"."Score" AS "SCORE", ANY_VALUE("t2"."DisplayName") AS "OWNERDISPLAYNAME", COUNT(CASE WHEN "t2"."Id1" IS NOT NULL THEN 1 ELSE NULL END) AS "COMMENTCOUNT", COUNT(DISTINCT "Posts0"."Id") AS "ANSWERCOUNT", COUNT(DISTINCT "Votes"."Id") AS "VOTECOUNT"
FROM (SELECT "s1"."Id", "s1"."PostTypeId", "s1"."AcceptedAnswerId", "s1"."ParentId", "s1"."CreationDate", "s1"."Score", "s1"."ViewCount", "s1"."Body", "s1"."OwnerUserId", "s1"."OwnerDisplayName", "s1"."LastEditorUserId", "s1"."LastEditorDisplayName", "s1"."LastEditDate", "s1"."LastActivityDate", "s1"."Title", "s1"."Tags", "s1"."AnswerCount", "s1"."CommentCount", "s1"."FavoriteCount", "s1"."ClosedDate", "s1"."CommunityOwnedDate", "s1"."ContentLicense", "s1"."Id0", "s1"."Reputation", "s1"."CreationDate0", "s1"."DisplayName", "s1"."LastAccessDate", "s1"."WebsiteUrl", "s1"."Location", "s1"."AboutMe", "s1"."Views", "s1"."UpVotes", "s1"."DownVotes", "s1"."ProfileImageUrl", "s1"."AccountId", "Comments"."Id" AS "Id1", "Comments"."PostId", "Comments"."Score" AS "Score0", "Comments"."Text", "Comments"."CreationDate" AS "CreationDate1", "Comments"."UserDisplayName", "Comments"."UserId", "Comments"."ContentLicense" AS "ContentLicense0"
FROM "STACK"."Comments"
RIGHT JOIN "s1" ON "Comments"."PostId" = "s1"."Id") AS "t2"
LEFT JOIN "STACK"."Posts" AS "Posts0" ON "t2"."Id" = "Posts0"."ParentId"
LEFT JOIN "STACK"."Votes" ON "t2"."Id" = "Votes"."PostId"
GROUP BY "t2"."Id", "t2"."Title", "t2"."CreationDate", "t2"."ViewCount", "t2"."Score", "t2"."DisplayName"
ORDER BY "t2"."CreationDate" DESC NULLS FIRST) AS "t6"