SELECT COALESCE("Users"."Id", "Users"."Id") AS "Id", "Users"."DisplayName", ANY_VALUE("Users"."Id") AS "USERID", COUNT(DISTINCT "t3"."POSTID") AS "POSTSCREATED", SUM("t3"."VOTECOUNT") AS "TOTALVOTES", SUM("t3"."COMMENTCOUNT") AS "TOTALCOMMENTS", MIN("t3"."POSTCREATIONDATE") AS "FIRSTPOSTDATE", MAX("t3"."POSTCREATIONDATE") AS "LASTPOSTDATE"
FROM "STACK"."Users"
INNER JOIN (SELECT ANY_VALUE("t0"."Id") AS "USERID", "t0"."DisplayName" AS "DISPLAYNAME", ANY_VALUE("t0"."Id0") AS "POSTID", "t0"."Title" AS "TITLE", ANY_VALUE("t0"."CreationDate0") AS "POSTCREATIONDATE", COUNT(CASE WHEN CAST("t0"."VoteTypeId" AS INTEGER) IN (2, 3) THEN 1 ELSE NULL END) AS "VOTECOUNT", COUNT(CASE WHEN "Comments"."Id" IS NOT NULL THEN 1 ELSE NULL END) AS "COMMENTCOUNT", COUNT(*) AS "TOTALPOSTS"
FROM (SELECT "t"."Id", "t"."Reputation", "t"."CreationDate", "t"."DisplayName", "t"."LastAccessDate", "t"."WebsiteUrl", "t"."Location", "t"."AboutMe", "t"."Views", "t"."UpVotes", "t"."DownVotes", "t"."ProfileImageUrl", "t"."AccountId", "t"."Id0", "t"."PostTypeId", "t"."AcceptedAnswerId", "t"."ParentId", "t"."CreationDate0", "t"."Score", "t"."ViewCount", "t"."Body", "t"."OwnerUserId", "t"."OwnerDisplayName", "t"."LastEditorUserId", "t"."LastEditorDisplayName", "t"."LastEditDate", "t"."LastActivityDate", "t"."Title", "t"."Tags", "t"."AnswerCount", "t"."CommentCount", "t"."FavoriteCount", "t"."ClosedDate", "t"."CommunityOwnedDate", "t"."ContentLicense", "Votes"."Id" AS "Id1", "Votes"."PostId", "Votes"."VoteTypeId", "Votes"."UserId", "Votes"."CreationDate" AS "CreationDate1", "Votes"."BountyAmount"
FROM "STACK"."Votes"
RIGHT JOIN (SELECT "Users0"."Id", "Users0"."Reputation", "Users0"."CreationDate", "Users0"."DisplayName", "Users0"."LastAccessDate", "Users0"."WebsiteUrl", "Users0"."Location", "Users0"."AboutMe", "Users0"."Views", "Users0"."UpVotes", "Users0"."DownVotes", "Users0"."ProfileImageUrl", "Users0"."AccountId", "Posts"."Id" AS "Id0", "Posts"."PostTypeId", "Posts"."AcceptedAnswerId", "Posts"."ParentId", "Posts"."CreationDate" AS "CreationDate0", "Posts"."Score", "Posts"."ViewCount", "Posts"."Body", "Posts"."OwnerUserId", "Posts"."OwnerDisplayName", "Posts"."LastEditorUserId", "Posts"."LastEditorDisplayName", "Posts"."LastEditDate", "Posts"."LastActivityDate", "Posts"."Title", "Posts"."Tags", "Posts"."AnswerCount", "Posts"."CommentCount", "Posts"."FavoriteCount", "Posts"."ClosedDate", "Posts"."CommunityOwnedDate", "Posts"."ContentLicense"
FROM "STACK"."Posts"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Posts"."OwnerUserId" = "Users0"."Id") AS "t" ON "Votes"."PostId" = "t"."Id0") AS "t0"
LEFT JOIN "STACK"."Comments" ON "t0"."Id0" = "Comments"."PostId"
GROUP BY "t0"."Id", "t0"."DisplayName", "t0"."Id0", "t0"."Title", "t0"."CreationDate0") AS "t3" ON "Users"."Id" = "t3"."USERID"
GROUP BY "Users"."Id", "Users"."DisplayName"