SELECT COALESCE("t8"."USERNAME", "t8"."USERNAME") AS "USERNAME", "t8"."REPUTATION", "t8"."TOTALVOTES", "t8"."UPVOTES", "t8"."DOWNVOTES", "t8"."POSTID", "t8"."TOTALCOMMENTS", "t8"."TOTALANSWERS", "t8"."TOTALUPVOTES", "t8"."TOTALDOWNVOTES"
FROM (SELECT "Users0"."DisplayName" AS "USERNAME", "Users0"."Reputation" AS "REPUTATION", "s1"."TOTALVOTES", "s1"."UPVOTES", "s1"."DOWNVOTES", "t6"."POSTID", "t6"."TOTALCOMMENTS", "t6"."TOTALANSWERS", "t6"."TOTALUPVOTES", "t6"."TOTALDOWNVOTES"
FROM "STACK"."Users" AS "Users0"
INNER JOIN "s1" ON "Users0"."Id" = "s1"."USERID"
INNER JOIN (SELECT ANY_VALUE("t3"."Id") AS "POSTID", "t3"."OwnerUserId" AS "OWNERUSERID", COUNT("t3"."Id0") AS "TOTALCOMMENTS", COUNT(DISTINCT CASE WHEN CAST("t3"."PostTypeId" AS INTEGER) = 2 THEN "t3"."Id" ELSE NULL END) AS "TOTALANSWERS", SUM(CASE WHEN CAST("Votes0"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "TOTALUPVOTES", SUM(CASE WHEN CAST("Votes0"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "TOTALDOWNVOTES"
FROM "STACK"."Votes" AS "Votes0"
RIGHT JOIN (SELECT "Posts"."Id", "Posts"."PostTypeId", "Posts"."AcceptedAnswerId", "Posts"."ParentId", "Posts"."CreationDate", "Posts"."Score", "Posts"."ViewCount", "Posts"."Body", "Posts"."OwnerUserId", "Posts"."OwnerDisplayName", "Posts"."LastEditorUserId", "Posts"."LastEditorDisplayName", "Posts"."LastEditDate", "Posts"."LastActivityDate", "Posts"."Title", "Posts"."Tags", "Posts"."AnswerCount", "Posts"."CommentCount", "Posts"."FavoriteCount", "Posts"."ClosedDate", "Posts"."CommunityOwnedDate", "Posts"."ContentLicense", "Comments"."Id" AS "Id0", "Comments"."PostId", "Comments"."Score" AS "Score0", "Comments"."Text", "Comments"."CreationDate" AS "CreationDate0", "Comments"."UserDisplayName", "Comments"."UserId", "Comments"."ContentLicense" AS "ContentLicense0"
FROM "STACK"."Comments"
RIGHT JOIN "STACK"."Posts" ON "Comments"."PostId" = "Posts"."Id") AS "t3" ON "Votes0"."PostId" = "t3"."Id"
GROUP BY "t3"."Id", "t3"."OwnerUserId") AS "t6" ON "Users0"."Id" = "t6"."OWNERUSERID"
ORDER BY "Users0"."Reputation" DESC NULLS FIRST, "s1"."TOTALVOTES" DESC NULLS FIRST) AS "t8"