SELECT COALESCE("t7"."POSTID", "t7"."POSTID") AS "POSTID", "t7"."TITLE", "t7"."POSTTYPEID", "t7"."CREATIONDATE", "t7"."VOTECOUNT", "t7"."COMMENTCOUNT", "t7"."BADGECOUNT", "t7"."UPVOTES", "t7"."DOWNVOTES", "t7"."OWNERDISPLAYNAME", "t7"."OWNERREPUTATION"
FROM (SELECT "t5"."POSTID", "t5"."TITLE", "t5"."POSTTYPEID", "t5"."CREATIONDATE", "t5"."VOTECOUNT", "t5"."COMMENTCOUNT", "t5"."BADGECOUNT", "t5"."UPVOTES", "t5"."DOWNVOTES", "Users"."DisplayName" AS "OWNERDISPLAYNAME", "Users"."Reputation" AS "OWNERREPUTATION"
FROM "STACK"."Users"
INNER JOIN (SELECT ANY_VALUE("s1"."Id") AS "POSTID", "s1"."Title" AS "TITLE", "s1"."PostTypeId" AS "POSTTYPEID", "s1"."CreationDate" AS "CREATIONDATE", COUNT("s1"."Id0") AS "VOTECOUNT", COUNT("s1"."Id1") AS "COMMENTCOUNT", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("s1"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("s1"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES"
FROM "s1"
LEFT JOIN "STACK"."Badges" ON "s1"."OwnerUserId" = "Badges"."UserId"
GROUP BY "s1"."Id", "s1"."Title", "s1"."PostTypeId", "s1"."CreationDate") AS "t5" ON "Users"."Id" = "t5"."POSTID"
ORDER BY "t5"."CREATIONDATE" DESC NULLS FIRST) AS "t7"