SELECT COALESCE("t10"."DISPLAYNAME", "t10"."DISPLAYNAME") AS "DISPLAYNAME", "t10"."TOTALVOTES", "t10"."UPVOTES", "t10"."DOWNVOTES", "t10"."POSTID", "t10"."TITLE", "t10"."SCORE", "t10"."VIEWCOUNT", "t10"."ANSWERCOUNT", "t10"."COMMENTCOUNT", "t10"."POSTUPVOTES", "t10"."POSTDOWNVOTES"
FROM (SELECT "t5"."DISPLAYNAME", "t5"."TOTALVOTES", "t5"."UPVOTES", "t5"."DOWNVOTES", "t8"."POSTID", "t8"."TITLE", "t8"."SCORE", "t8"."VIEWCOUNT", "t8"."ANSWERCOUNT", "t8"."COMMENTCOUNT", "t2"."POSTUPVOTES", "t2"."POSTDOWNVOTES"
FROM (SELECT ANY_VALUE("Id") AS "POSTID", SUM("FD_COL_1") AS "POSTUPVOTES", SUM("FD_COL_2") AS "POSTDOWNVOTES"
FROM "s1"
GROUP BY "Id") AS "t2"
INNER JOIN ((SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Votes0"."Id") AS "TOTALVOTES", SUM(CASE WHEN CAST("Votes0"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("Votes0"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES"
FROM "STACK"."Votes" AS "Votes0"
RIGHT JOIN "STACK"."Users" ON "Votes0"."UserId" = "Users"."Id"
GROUP BY "Users"."Id", "Users"."DisplayName") AS "t5" INNER JOIN (SELECT ANY_VALUE("Posts0"."Id") AS "POSTID", "Posts0"."Title" AS "TITLE", "Posts0"."OwnerUserId" AS "OWNERUSERID", "Posts0"."Score" AS "SCORE", "Posts0"."ViewCount" AS "VIEWCOUNT", "Posts0"."AnswerCount" AS "ANSWERCOUNT", COUNT("Comments"."Id") AS "COMMENTCOUNT"
FROM "STACK"."Comments"
RIGHT JOIN "STACK"."Posts" AS "Posts0" ON "Comments"."PostId" = "Posts0"."Id"
GROUP BY "Posts0"."Id", "Posts0"."Score", "Posts0"."ViewCount", "Posts0"."OwnerUserId", "Posts0"."Title", "Posts0"."AnswerCount") AS "t8" ON "t5"."USERID" = "t8"."OWNERUSERID") ON "t2"."POSTID" = "t8"."POSTID"
ORDER BY "t5"."TOTALVOTES" DESC NULLS FIRST, "t8"."SCORE" DESC NULLS FIRST) AS "t10"