SELECT COALESCE("t6"."USERID", "t6"."USERID") AS "USERID", "t6"."DISPLAYNAME", "t6"."TOTALPOSTS", "t6"."TOTALQUESTIONS", "t6"."TOTALANSWERS", "t6"."TOTALSCORE", "t6"."AVGVIEWCOUNT", "t6"."TOTALBADGES", "t6"."GOLDBADGES", "t6"."SILVERBADGES", "t6"."BRONZEBADGES"
FROM (SELECT "t2"."USERID", "t2"."DISPLAYNAME", "t2"."TOTALPOSTS", "t2"."TOTALQUESTIONS", "t2"."TOTALANSWERS", "t2"."TOTALSCORE", "t2"."AVGVIEWCOUNT", "t4"."TOTALBADGES", "t4"."GOLDBADGES", "t4"."SILVERBADGES", "t4"."BRONZEBADGES"
FROM (SELECT ANY_VALUE("Id") AS "USERID", "DISPLAYNAME", COUNT("Id0") AS "TOTALPOSTS", SUM("FD_COL_3") AS "TOTALQUESTIONS", SUM("FD_COL_4") AS "TOTALANSWERS", SUM("Score") AS "TOTALSCORE", AVG("ViewCount") AS "AVGVIEWCOUNT"
FROM "s1"
GROUP BY "Id", "DISPLAYNAME") AS "t2"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t4" ON "t2"."USERID" = "t4"."USERID"
ORDER BY "t2"."TOTALPOSTS" DESC NULLS FIRST
FETCH NEXT 100 ROWS ONLY) AS "t6"