SELECT COALESCE("t6"."DISPLAYNAME", "t6"."DISPLAYNAME") AS "DISPLAYNAME", "t6"."TOTALPOSTS", "t6"."TOTALQUESTIONS", "t6"."TOTALANSWERS", "t6"."TOTALVIEWS", "t6"."TOTALSCORE", "t6"."POSTTYPENAME", "t6"."POSTCOUNT"
FROM (SELECT "s1"."DISPLAYNAME", "s1"."TOTALPOSTS", "s1"."TOTALQUESTIONS", "s1"."TOTALANSWERS", "s1"."TOTALVIEWS", "s1"."TOTALSCORE", "t4"."POSTTYPENAME", "t4"."POSTCOUNT"
FROM "s1"
LEFT JOIN (SELECT ANY_VALUE("PostTypes"."Id") AS "POSTTYPEID", ANY_VALUE("PostTypes"."Name") AS "POSTTYPENAME", COUNT(*) AS "POSTCOUNT"
FROM "STACK"."PostTypes"
INNER JOIN "STACK"."Posts" AS "Posts0" ON "PostTypes"."Id" = "Posts0"."PostTypeId"
GROUP BY "PostTypes"."Id", "PostTypes"."Name") AS "t4" ON "s1"."FD_COL_7"
ORDER BY "s1"."TOTALSCORE" DESC NULLS FIRST, "s1"."TOTALPOSTS" DESC NULLS FIRST) AS "t6"