SELECT COALESCE("t2"."Id", "t2"."Id") AS "USERID", "t2"."DisplayName" AS "DISPLAYNAME", CASE WHEN "t2"."BADGECOUNT" IS NOT NULL THEN CAST("t2"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t4"."POSTCOUNT" IS NOT NULL THEN CAST("t4"."POSTCOUNT" AS BIGINT) ELSE 0 END AS "POSTCOUNT", CASE WHEN "t4"."QUESTIONCOUNT" IS NOT NULL THEN CAST("t4"."QUESTIONCOUNT" AS BIGINT) ELSE 0 END AS "QUESTIONCOUNT", CASE WHEN "t4"."ANSWERCOUNT" IS NOT NULL THEN CAST("t4"."ANSWERCOUNT" AS BIGINT) ELSE 0 END AS "ANSWERCOUNT", CASE WHEN "t4"."TOTALSCORE" IS NOT NULL THEN CAST("t4"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE", CASE WHEN "t4"."AVERAGEVIEWS" IS NOT NULL THEN CAST("t4"."AVERAGEVIEWS" AS INTEGER) ELSE 0 END AS "AVERAGEVIEWS", CASE WHEN "t6"."RECENTACTIVITYCOUNT" IS NOT NULL THEN CAST("t6"."RECENTACTIVITYCOUNT" AS BIGINT) ELSE 0 END AS "RECENTACTIVITYCOUNT"
FROM (SELECT *
FROM "STACK"."Users"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Badges"."UserId" = "Users0"."Id"
GROUP BY "Users0"."Id", "Users0"."DisplayName") AS "t1" ON "Users"."Id" = "t1"."USERID"
WHERE CASE WHEN "t1"."BADGECOUNT" IS NOT NULL THEN CAST("t1"."BADGECOUNT" AS BIGINT) ELSE 0 END > 0) AS "t2"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "POSTCOUNT", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "QUESTIONCOUNT", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "ANSWERCOUNT", SUM("Score") AS "TOTALSCORE", AVG("ViewCount") AS "AVERAGEVIEWS"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t4" ON "t2"."Id" = "t4"."OWNERUSERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(CASE WHEN "LastActivityDate" >= (TIMESTAMP '2024-10-01 12:34:56' - INTERVAL '30' DAY) THEN 1 ELSE NULL END) AS "RECENTACTIVITYCOUNT"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t6" ON "t2"."Id" = "t6"."OWNERUSERID"