SELECT COALESCE("t"."EXPR$0", "t"."EXPR$0") AS "FD_COL_0", "Users0"."Id", "Users0"."Reputation", "Users0"."CreationDate", "Users0"."DisplayName", "Users0"."LastAccessDate", "Users0"."WebsiteUrl", "Users0"."Location", "Users0"."AboutMe", "Users0"."Views", "Users0"."UpVotes", "Users0"."DownVotes", "Users0"."ProfileImageUrl", "Users0"."AccountId", "t1"."OWNERUSERID", "t1"."TOTALPOSTS", "t1"."ANSWEREDQUESTIONS", "t1"."QUESTIONS", "t1"."POPULARPOSTS", "t4"."USERID", "t4"."DISPLAYNAME" AS "DISPLAYNAME_", "t4"."BADGECOUNT", "t4"."GOLDBADGES", "t4"."SILVERBADGES", "t4"."BRONZEBADGES"
FROM (SELECT MAX("LastAccessDate") AS "EXPR$0"
FROM "STACK"."Users") AS "t"
INNER JOIN ("STACK"."Users" AS "Users0" LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", COUNT(DISTINCT "ParentId") AS "ANSWEREDQUESTIONS", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "QUESTIONS", SUM(CASE WHEN "ViewCount" > 100 THEN 1 ELSE 0 END) AS "POPULARPOSTS"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t1" ON "Users0"."Id" = "t1"."OWNERUSERID" LEFT JOIN (SELECT ANY_VALUE("Users1"."Id") AS "USERID", "Users1"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" AS "Users1" ON "Badges"."UserId" = "Users1"."Id"
GROUP BY "Users1"."Id", "Users1"."DisplayName") AS "t4" ON "Users0"."Id" = "t4"."USERID") ON "t"."EXPR$0" = "Users0"."LastAccessDate"