SELECT COALESCE("t8"."USERID", "t8"."USERID") AS "USERID", "t8"."DISPLAYNAME", "t8"."TOTALPOSTS", "t8"."ANSWEREDQUESTIONS", "t8"."QUESTIONSPOSTED", "t8"."BADGECOUNT", "t8"."GOLDBADGES", "t8"."SILVERBADGES", "t8"."BRONZEBADGES", "t8"."USERSTATUS", "t8"."BADGELEVEL"
FROM (SELECT "s1"."Id" AS "USERID", "s1"."DisplayName" AS "DISPLAYNAME", CASE WHEN "s1"."TOTALPOSTS" IS NOT NULL THEN CAST("s1"."TOTALPOSTS" AS BIGINT) ELSE 0 END AS "TOTALPOSTS", CASE WHEN "s1"."ANSWEREDQUESTIONS" IS NOT NULL THEN CAST("s1"."ANSWEREDQUESTIONS" AS BIGINT) ELSE 0 END AS "ANSWEREDQUESTIONS", CASE WHEN "s1"."QUESTIONS" IS NOT NULL THEN CAST("s1"."QUESTIONS" AS INTEGER) ELSE 0 END AS "QUESTIONSPOSTED", CASE WHEN "s1"."BADGECOUNT" IS NOT NULL THEN CAST("s1"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "s1"."GOLDBADGES" IS NOT NULL THEN CAST("s1"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "s1"."SILVERBADGES" IS NOT NULL THEN CAST("s1"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "s1"."BRONZEBADGES" IS NOT NULL THEN CAST("s1"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN CAST("s1"."ANSWEREDQUESTIONS" AS BIGINT) > 0 THEN 'Active Contributor' ELSE 'Lurker            ' END AS "USERSTATUS", CASE WHEN CAST("s1"."BADGECOUNT" AS BIGINT) = 0 OR "s1"."BADGECOUNT" IS NULL THEN 'No Badges   ' WHEN CAST("s1"."BADGECOUNT" AS BIGINT) >= 1 AND CAST("s1"."BADGECOUNT" AS BIGINT) <= 3 OR 0 >= 1 AND 0 <= 3 AND "s1"."BADGECOUNT" IS NULL THEN 'Novice      ' WHEN CAST("s1"."BADGECOUNT" AS BIGINT) >= 4 AND CAST("s1"."BADGECOUNT" AS BIGINT) <= 10 OR 0 >= 4 AND 0 <= 10 AND "s1"."BADGECOUNT" IS NULL THEN 'Intermediate' ELSE 'Expert      ' END AS "BADGELEVEL"
FROM (SELECT AVG("Reputation") AS "EXPR$0"
FROM "STACK"."Users") AS "t6"
INNER JOIN "s1" ON "t6"."EXPR$0" < "s1"."Reputation"
ORDER BY 3 DESC NULLS FIRST
OFFSET 0 ROWS
FETCH NEXT 10 ROWS ONLY) AS "t8"