SELECT COALESCE("t6"."USERID", "t6"."USERID") AS "USERID", "t6"."DISPLAYNAME", "t6"."BADGECOUNT", "t6"."GOLDBADGES", "t6"."SILVERBADGES", "t6"."BRONZEBADGES", "t6"."TOTALPOSTS", "t6"."TOTALCOMMENTS", "t6"."TOTALVOTES", "t6"."TOTALVIEWS", "t6"."TOTALSCORE", "t6"."SCOREPERPOST", "t6"."VIEWSPERPOST"
FROM (SELECT "Users"."Id" AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", CASE WHEN "t3"."BADGECOUNT" IS NOT NULL THEN CAST("t3"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t3"."GOLDBADGES" IS NOT NULL THEN CAST("t3"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t3"."SILVERBADGES" IS NOT NULL THEN CAST("t3"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t3"."BRONZEBADGES" IS NOT NULL THEN CAST("t3"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN "t4"."TOTALPOSTS" IS NOT NULL THEN CAST("t4"."TOTALPOSTS" AS BIGINT) ELSE 0 END AS "TOTALPOSTS", CASE WHEN "t4"."TOTALCOMMENTS" IS NOT NULL THEN CAST("t4"."TOTALCOMMENTS" AS BIGINT) ELSE 0 END AS "TOTALCOMMENTS", CASE WHEN "t4"."TOTALVOTES" IS NOT NULL THEN CAST("t4"."TOTALVOTES" AS BIGINT) ELSE 0 END AS "TOTALVOTES", CASE WHEN "t4"."TOTALVIEWS" IS NOT NULL THEN CAST("t4"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS "TOTALVIEWS", CASE WHEN "t4"."TOTALSCORE" IS NOT NULL THEN CAST("t4"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE", CAST(CASE WHEN "t4"."TOTALSCORE" IS NOT NULL THEN CAST("t4"."TOTALSCORE" AS INTEGER) ELSE 0 END AS DECIMAL(12, 1)) / CASE WHEN CAST("t4"."TOTALPOSTS" AS BIGINT) = 0 OR "t4"."TOTALPOSTS" IS NULL THEN NULL ELSE CASE WHEN "t4"."TOTALPOSTS" IS NOT NULL THEN CAST("t4"."TOTALPOSTS" AS BIGINT) ELSE 0 END END AS "SCOREPERPOST", CAST(CASE WHEN "t4"."TOTALVIEWS" IS NOT NULL THEN CAST("t4"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS DECIMAL(12, 1)) / CASE WHEN CAST("t4"."TOTALPOSTS" AS BIGINT) = 0 OR "t4"."TOTALPOSTS" IS NULL THEN NULL ELSE CASE WHEN "t4"."TOTALPOSTS" IS NOT NULL THEN CAST("t4"."TOTALPOSTS" AS BIGINT) ELSE 0 END END AS "VIEWSPERPOST"
FROM "STACK"."Users"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Badges"."UserId" = "Users0"."Id"
GROUP BY "Users0"."Id", "Users0"."DisplayName") AS "t3" ON "Users"."Id" = "t3"."USERID"
LEFT JOIN (SELECT "OwnerUserId", COUNT(*) AS "TOTALPOSTS", COUNT("Id00") AS "TOTALCOMMENTS", COUNT(DISTINCT "Id") AS "TOTALVOTES", SUM("ViewCount") AS "TOTALVIEWS", SUM("Score") AS "TOTALSCORE"
FROM "s1"
GROUP BY "OwnerUserId") AS "t4" ON "Users"."Id" = "t4"."OwnerUserId"
ORDER BY 11 DESC NULLS FIRST, 3 DESC NULLS FIRST
FETCH NEXT 10 ROWS ONLY) AS "t6"