SELECT COALESCE("t12"."POSTID", "t12"."POSTID") AS "POSTID", "t12"."TITLE", "t12"."SCORE", "t12"."CREATIONDATE", "t12"."UPVOTES", "t12"."DOWNVOTES", "t12"."USERBADGECOUNT", "t12"."LASTCLOSEDATE", "t12"."CLOSEREASONNAMES", "t12"."POSTSTATUS"
FROM (SELECT "t5"."POSTID", "t5"."TITLE", "t5"."SCORE", "t5"."CREATIONDATE", "t5"."UPVOTES", "t5"."DOWNVOTES", "t5"."USERBADGECOUNT", "t10"."LASTCLOSEDATE", "t10"."CLOSEREASONNAMES", CASE WHEN "t10"."LASTCLOSEDATE" IS NOT NULL THEN 'Closed' ELSE 'Open  ' END AS "POSTSTATUS"
FROM (SELECT "s1"."Id" AS "POSTID", "s1"."Title" AS "TITLE", "s1"."Score" AS "SCORE", "s1"."CreationDate" AS "CREATIONDATE", CASE WHEN "s1"."UPVOTES" IS NOT NULL THEN CAST("s1"."UPVOTES" AS INTEGER) ELSE 0 END AS "UPVOTES", CASE WHEN "s1"."DOWNVOTES" IS NOT NULL THEN CAST("s1"."DOWNVOTES" AS INTEGER) ELSE 0 END AS "DOWNVOTES", CASE WHEN "t4"."BADGECOUNT" IS NOT NULL THEN CAST("t4"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "USERBADGECOUNT"
FROM "s1"
LEFT JOIN "STACK"."Users" ON "s1"."OwnerUserId" = "Users"."Id"
LEFT JOIN (SELECT "UserId", COUNT(*) AS "BADGECOUNT"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t4" ON "Users"."Id" = "t4"."UserId") AS "t5"
LEFT JOIN (SELECT "t7"."PostId" AS "POSTID", MAX("t7"."CreationDate") AS "LASTCLOSEDATE", LISTAGG("t6"."Name", ', ') AS "CLOSEREASONNAMES"
FROM (SELECT "Id", "Name", CAST("Id" AS INTEGER) AS "Id0"
FROM "STACK"."CloseReasonTypes") AS "t6"
INNER JOIN (SELECT "Id", "PostHistoryTypeId", "PostId", "RevisionGUID", "CreationDate", "UserId", "UserDisplayName", "Comment", "Text", "ContentLicense", CAST("Comment" AS INTEGER) AS "Comment0"
FROM "STACK"."PostHistory") AS "t7" ON "t6"."Id0" = "t7"."Comment0"
WHERE CAST("t7"."PostHistoryTypeId" AS INTEGER) = 10
GROUP BY "t7"."PostId") AS "t10" ON "t5"."POSTID" = "t10"."POSTID"
ORDER BY "t5"."SCORE" DESC NULLS FIRST, "t5"."CREATIONDATE" DESC NULLS FIRST
FETCH NEXT 100 ROWS ONLY) AS "t12"