SELECT COALESCE("t10"."USERNAME", "t10"."USERNAME") AS "USERNAME", "t10"."POSTSCREATED", "t10"."TOTALSCORE", "t10"."AVGVIEWCOUNT", "t10"."POSTTITLE", "t10"."POSTHISTORYTYPEID", "t10"."ACTIONDATE", "t10"."ACTIONTYPE", "t10"."POSTTAG", "t10"."TAGPOSTCOUNT", "t10"."TAGTOTALVIEWS"
FROM (SELECT "t6"."DISPLAYNAME" AS "USERNAME", "t6"."POSTSCREATED", "t6"."TOTALSCORE", "t6"."AVGVIEWCOUNT", "t6"."Title" AS "POSTTITLE", "t6"."POSTHISTORYTYPEID", "t6"."CREATIONDATE" AS "ACTIONDATE", CASE WHEN CAST("t6"."POSTHISTORYTYPEID" AS INTEGER) = 10 THEN 'Closed      ' WHEN CAST("t6"."POSTHISTORYTYPEID" AS INTEGER) = 11 THEN 'Reopened    ' WHEN CAST("t6"."POSTHISTORYTYPEID" AS INTEGER) = 12 THEN 'Deleted     ' ELSE 'Other Action' END AS "ACTIONTYPE", "t6"."TagName" AS "POSTTAG", "t8"."POSTCOUNT" AS "TAGPOSTCOUNT", "t8"."TOTALVIEWS" AS "TAGTOTALVIEWS"
FROM (SELECT "s1"."USERID", "s1"."DISPLAYNAME", "s1"."POSTSCREATED", "s1"."TOTALSCORE", "s1"."AVGVIEWCOUNT", "t5"."POSTID", "t5"."POSTHISTORYTYPEID", "t5"."CREATIONDATE", "t5"."PREVIOUSACTION", "t5"."ROWNUM", "Posts0"."Id", "Posts0"."PostTypeId", "Posts0"."AcceptedAnswerId", "Posts0"."ParentId", "Posts0"."CreationDate", "Posts0"."Score", "Posts0"."ViewCount", "Posts0"."Body", "Posts0"."OwnerUserId", "Posts0"."OwnerDisplayName", "Posts0"."LastEditorUserId", "Posts0"."LastEditorDisplayName", "Posts0"."LastEditDate", "Posts0"."LastActivityDate", "Posts0"."Title", "Posts0"."Tags", "Posts0"."AnswerCount", "Posts0"."CommentCount", "Posts0"."FavoriteCount", "Posts0"."ClosedDate", "Posts0"."CommunityOwnedDate", "Posts0"."ContentLicense", "Tags"."Id" AS "Id0", "Tags"."TagName", "Tags"."Count", "Tags"."ExcerptPostId", "Tags"."WikiPostId", "Tags"."IsModeratorOnly", "Tags"."IsRequired"
FROM "STACK"."Tags"
RIGHT JOIN ("STACK"."Posts" AS "Posts0" INNER JOIN ((SELECT "PostId" AS "POSTID", "PostHistoryTypeId" AS "POSTHISTORYTYPEID", "CreationDate" AS "CREATIONDATE", CAST(NULL AS VARCHAR(400) CHARACTER SET "ISO-8859-1") AS "PREVIOUSACTION", ROW_NUMBER() OVER (PARTITION BY "PostId" ORDER BY "CreationDate" DESC NULLS FIRST) AS "ROWNUM"
FROM "STACK"."PostHistory"
WHERE CAST("PostHistoryTypeId" AS INTEGER) IN (10, 11, 12)) AS "t5" INNER JOIN "s1" ON "t5"."POSTID" = "s1"."USERID") ON "Posts0"."Id" = "t5"."POSTID") ON "Posts0"."Tags" LIKE '%' || "Tags"."TagName" || '%') AS "t6"
LEFT JOIN (SELECT "Tags0"."TagName" AS "TAGNAME", COUNT("Posts1"."Id") AS "POSTCOUNT", SUM(CASE WHEN "Posts1"."ViewCount" IS NOT NULL THEN CAST("Posts1"."ViewCount" AS INTEGER) ELSE 0 END) AS "TOTALVIEWS"
FROM "STACK"."Posts" AS "Posts1"
RIGHT JOIN "STACK"."Tags" AS "Tags0" ON "Posts1"."Tags" LIKE '%' || "Tags0"."TagName" || '%'
GROUP BY "Tags0"."TagName") AS "t8" ON "t6"."TagName" = "t8"."TAGNAME"
ORDER BY "t6"."TOTALSCORE" DESC NULLS FIRST, "t6"."CREATIONDATE" DESC NULLS FIRST) AS "t10"