SELECT COALESCE("t10"."USERID", "t10"."USERID") AS "USERID", "t10"."DISPLAYNAME", "t10"."TOTALPOSTS", "t10"."QUESTIONCOUNT", "t10"."ANSWERCOUNT", "t10"."TOTALVIEWS", "t10"."TOTALSCORE", "t10"."GOLDBADGES", "t10"."SILVERBADGES", "t10"."BRONZEBADGES", "t10"."CLOSEREOPENCOUNT", "t10"."EDITSUGGESTIONCOUNT", "t10"."LASTPOSTDATE"
FROM (SELECT "t8"."USERID", "t8"."DISPLAYNAME", "t8"."TOTALPOSTS", "t8"."QUESTIONCOUNT", "t8"."ANSWERCOUNT", "t8"."TOTALVIEWS", "t8"."TOTALSCORE", "t8"."GOLDBADGES", "t8"."SILVERBADGES", "t8"."BRONZEBADGES", "t3"."CLOSEREOPENCOUNT", "t3"."EDITSUGGESTIONCOUNT", "t8"."LASTPOSTDATE"
FROM (SELECT "UserId" AS "USERID", COUNT(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) IN (10, 11) THEN 1 ELSE NULL END) AS "CLOSEREOPENCOUNT", COUNT(CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) IN (24, 25) THEN 1 ELSE NULL END) AS "EDITSUGGESTIONCOUNT"
FROM "STACK"."PostHistory"
GROUP BY "UserId") AS "t3"
RIGHT JOIN (SELECT "t7"."USERID", "t7"."DISPLAYNAME", "t7"."TOTALPOSTS", "t7"."QUESTIONCOUNT", "t7"."ANSWERCOUNT", "t7"."TOTALVIEWS", "t7"."TOTALSCORE", "t7"."LASTPOSTDATE", "s1"."USERID" AS "USERID0", "s1"."GOLDBADGES", "s1"."SILVERBADGES", "s1"."BRONZEBADGES"
FROM "s1"
RIGHT JOIN (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT("Posts"."Id") AS "TOTALPOSTS", COUNT(DISTINCT CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN "Posts"."Id" ELSE NULL END) AS "QUESTIONCOUNT", COUNT(DISTINCT CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN "Posts"."Id" ELSE NULL END) AS "ANSWERCOUNT", SUM("Posts"."ViewCount") AS "TOTALVIEWS", SUM("Posts"."Score") AS "TOTALSCORE", MAX("Posts"."CreationDate") AS "LASTPOSTDATE"
FROM "STACK"."Posts"
RIGHT JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id"
GROUP BY "Users"."Id", "Users"."DisplayName"
HAVING COUNT("Posts"."Id") > 0) AS "t7" ON "s1"."USERID" = "t7"."USERID") AS "t8" ON "t3"."USERID" = "t8"."USERID"
ORDER BY "t8"."TOTALSCORE" DESC NULLS FIRST, "t8"."LASTPOSTDATE" DESC NULLS FIRST
FETCH NEXT 100 ROWS ONLY) AS "t10"