SELECT COALESCE("t10"."USERID", "t10"."USERID") AS "USERID", "t10"."DISPLAYNAME", "t10"."REPUTATION", "t10"."TOTALPOSTS", "t10"."TOTALQUESTIONS", "t10"."TOTALANSWERS", "t10"."TOTALUPVOTES", "t10"."TOTALDOWNVOTES", "t10"."TOTALBADGES", "t10"."GOLDBADGES", "t10"."SILVERBADGES", "t10"."BRONZEBADGES", "t10"."TOTALEDITS", "t10"."TOTALCLOSEDPOSTS"
FROM (SELECT "s1"."USERID", "s1"."DISPLAYNAME", "s1"."REPUTATION", "s1"."TOTALPOSTS", "s1"."TOTALQUESTIONS", "s1"."TOTALANSWERS", "s1"."TOTALUPVOTES", "s1"."TOTALDOWNVOTES", CASE WHEN "s1"."TOTALBADGES" IS NOT NULL THEN CAST("s1"."TOTALBADGES" AS BIGINT) ELSE 0 END AS "TOTALBADGES", CASE WHEN "s1"."GOLDBADGES" IS NOT NULL THEN CAST("s1"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "s1"."SILVERBADGES" IS NOT NULL THEN CAST("s1"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "s1"."BRONZEBADGES" IS NOT NULL THEN CAST("s1"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN "t8"."TOTALEDITS" IS NOT NULL THEN CAST("t8"."TOTALEDITS" AS BIGINT) ELSE 0 END AS "TOTALEDITS", CASE WHEN "t8"."TOTALCLOSEDPOSTS" IS NOT NULL THEN CAST("t8"."TOTALCLOSEDPOSTS" AS BIGINT) ELSE 0 END AS "TOTALCLOSEDPOSTS"
FROM (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALEDITS", COUNT(DISTINCT CASE WHEN CAST("PostHistoryTypeId" AS INTEGER) = 10 THEN "Id" ELSE NULL END) AS "TOTALCLOSEDPOSTS"
FROM "STACK"."PostHistory"
GROUP BY "UserId") AS "t8"
RIGHT JOIN "s1" ON "t8"."USERID" = "s1"."USERID"
ORDER BY "s1"."REPUTATION" DESC NULLS FIRST
FETCH NEXT 50 ROWS ONLY) AS "t10"