SELECT COALESCE("t5"."USERID", "t5"."USERID") AS "USERID", "t5"."DISPLAYNAME", "t5"."TOTALBADGES", "t5"."TOTALPOSTS", "t5"."QUESTIONS", "t5"."ANSWERS", "t5"."TOTALSCORE", "t5"."TOTALVIEWCOUNT"
FROM (SELECT "Users"."Id" AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", CASE WHEN "t1"."TOTALBADGES" IS NOT NULL THEN CAST("t1"."TOTALBADGES" AS BIGINT) ELSE 0 END AS "TOTALBADGES", CASE WHEN "t3"."TOTALPOSTS" IS NOT NULL THEN CAST("t3"."TOTALPOSTS" AS BIGINT) ELSE 0 END AS "TOTALPOSTS", CASE WHEN "t3"."QUESTIONS" IS NOT NULL THEN CAST("t3"."QUESTIONS" AS BIGINT) ELSE 0 END AS "QUESTIONS", CASE WHEN "t3"."ANSWERS" IS NOT NULL THEN CAST("t3"."ANSWERS" AS BIGINT) ELSE 0 END AS "ANSWERS", CASE WHEN "t3"."TOTALSCORE" IS NOT NULL THEN CAST("t3"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE", CASE WHEN "t3"."TOTALVIEWCOUNT" IS NOT NULL THEN CAST("t3"."TOTALVIEWCOUNT" AS INTEGER) ELSE 0 END AS "TOTALVIEWCOUNT"
FROM "STACK"."Users"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", COUNT("Badges"."Id") AS "TOTALBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Badges"."UserId" = "Users0"."Id"
GROUP BY "Users0"."Id") AS "t1" ON "Users"."Id" = "t1"."USERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "QUESTIONS", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "ANSWERS", SUM("Score") AS "TOTALSCORE", SUM("ViewCount") AS "TOTALVIEWCOUNT"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t3" ON "Users"."Id" = "t3"."OWNERUSERID"
ORDER BY 7 DESC NULLS FIRST, 3 DESC NULLS FIRST
FETCH NEXT 10 ROWS ONLY) AS "t5"