SELECT COALESCE("t1"."Id", "t1"."Id") AS "USERID", "t1"."DisplayName" AS "DISPLAYNAME", CASE WHEN "t1"."TOTALPOSTS" IS NOT NULL THEN CAST("t1"."TOTALPOSTS" AS BIGINT) ELSE 0 END AS "TOTALPOSTS", CASE WHEN "t1"."QUESTIONS" IS NOT NULL THEN CAST("t1"."QUESTIONS" AS INTEGER) ELSE 0 END AS "QUESTIONS", CASE WHEN "t1"."ANSWERS" IS NOT NULL THEN CAST("t1"."ANSWERS" AS INTEGER) ELSE 0 END AS "ANSWERS", CASE WHEN "t1"."TOTALSCORE" IS NOT NULL THEN CAST("t1"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE", CASE WHEN "t1"."TOTALVIEWS" IS NOT NULL THEN CAST("t1"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS "TOTALVIEWS", CASE WHEN "t4"."BADGECOUNT" IS NOT NULL THEN CAST("t4"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t4"."GOLDBADGES" IS NOT NULL THEN CAST("t4"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t4"."SILVERBADGES" IS NOT NULL THEN CAST("t4"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t4"."BRONZEBADGES" IS NOT NULL THEN CAST("t4"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES"
FROM (SELECT *
FROM "STACK"."Users"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "QUESTIONS", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "ANSWERS", SUM("Score") AS "TOTALSCORE", SUM("ViewCount") AS "TOTALVIEWS"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t0" ON "Users"."Id" = "t0"."OWNERUSERID"
WHERE CASE WHEN "t0"."TOTALPOSTS" IS NOT NULL THEN CAST("t0"."TOTALPOSTS" AS BIGINT) ELSE 0 END > 0) AS "t1"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Badges"."UserId" = "Users0"."Id"
GROUP BY "Users0"."Id", "Users0"."DisplayName") AS "t4" ON "t1"."Id" = "t4"."USERID"