SELECT COALESCE("t8"."USERID", "t8"."USERID") AS "USERID", "t8"."DISPLAYNAME", "t8"."POSTCOUNT", "t8"."QUESTIONCOUNT", "t8"."ANSWERCOUNT", "t8"."TOTALBADGES", "t8"."GOLDBADGES", "t8"."SILVERBADGES", "t8"."BRONZEBADGES", "t8"."UPVOTES", "t8"."DOWNVOTES"
FROM (SELECT "t6"."USERID", "t6"."DISPLAYNAME", "t6"."POSTCOUNT", "t6"."QUESTIONCOUNT", "t6"."ANSWERCOUNT", CASE WHEN "t3"."BADGECOUNT" IS NOT NULL THEN CAST("t3"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "TOTALBADGES", CASE WHEN "t3"."GOLDBADGES" IS NOT NULL THEN CAST("t3"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t3"."SILVERBADGES" IS NOT NULL THEN CAST("t3"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t3"."BRONZEBADGES" IS NOT NULL THEN CAST("t3"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", "t6"."UPVOTES", "t6"."DOWNVOTES"
FROM (SELECT "UserId" AS "USERID", COUNT(*) AS "BADGECOUNT", SUM(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t3"
RIGHT JOIN (SELECT ANY_VALUE("Id") AS "USERID", "DISPLAYNAME", COUNT(DISTINCT "Id0") AS "POSTCOUNT", SUM("FD_COL_3") AS "QUESTIONCOUNT", SUM("FD_COL_4") AS "ANSWERCOUNT", SUM("FD_COL_5") AS "UPVOTES", SUM("FD_COL_6") AS "DOWNVOTES"
FROM "s1"
GROUP BY "Id", "DISPLAYNAME"
HAVING COUNT(DISTINCT "Id0") > 10) AS "t6" ON "t3"."USERID" = "t6"."USERID"
ORDER BY "t6"."UPVOTES" DESC NULLS FIRST, "t6"."DOWNVOTES"
FETCH NEXT 100 ROWS ONLY) AS "t8"