SELECT COALESCE("t7"."USERID", "t7"."USERID") AS "USERID", "t7"."DISPLAYNAME", "t7"."TOTALBADGES", "t7"."TOTALPOSTS", "t7"."TOTALQUESTIONS", "t7"."TOTALANSWERS", "t7"."TOTALTAGWIKIS", "t7"."POSITIVEPOSTS", "t7"."CONTRIBUTORSTATUS"
FROM (SELECT "Users0"."Id" AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", CASE WHEN "s1"."TOTALBADGES" IS NOT NULL THEN CAST("s1"."TOTALBADGES" AS BIGINT) ELSE 0 END AS "TOTALBADGES", CASE WHEN "t4"."TOTALPOSTS" IS NOT NULL THEN CAST("t4"."TOTALPOSTS" AS BIGINT) ELSE 0 END AS "TOTALPOSTS", CASE WHEN "t4"."TOTALQUESTIONS" IS NOT NULL THEN CAST("t4"."TOTALQUESTIONS" AS INTEGER) ELSE 0 END AS "TOTALQUESTIONS", CASE WHEN "t4"."TOTALANSWERS" IS NOT NULL THEN CAST("t4"."TOTALANSWERS" AS INTEGER) ELSE 0 END AS "TOTALANSWERS", CASE WHEN "t4"."TOTALTAGWIKIS" IS NOT NULL THEN CAST("t4"."TOTALTAGWIKIS" AS INTEGER) ELSE 0 END AS "TOTALTAGWIKIS", CASE WHEN "t4"."POSITIVEPOSTS" IS NOT NULL THEN CAST("t4"."POSITIVEPOSTS" AS INTEGER) ELSE 0 END AS "POSITIVEPOSTS", CASE WHEN CAST("s1"."TOTALBADGES" AS BIGINT) > 10 THEN 'Active Contributor' WHEN CAST("t4"."TOTALPOSTS" AS BIGINT) > 100 THEN 'Veteran           ' ELSE 'New Contributor   ' END AS "CONTRIBUTORSTATUS"
FROM "STACK"."Users" AS "Users0"
LEFT JOIN "s1" ON "Users0"."Id" = "s1"."USERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "TOTALQUESTIONS", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "TOTALANSWERS", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 4 THEN 1 ELSE 0 END) AS "TOTALTAGWIKIS", SUM(CASE WHEN "Score" > 0 THEN 1 ELSE 0 END) AS "POSITIVEPOSTS"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t4" ON "Users0"."Id" = "t4"."OWNERUSERID"
WHERE CASE WHEN "t4"."TOTALPOSTS" IS NOT NULL THEN CAST("t4"."TOTALPOSTS" AS BIGINT) ELSE 0 END > 0
ORDER BY 4 DESC NULLS FIRST, 3 DESC NULLS FIRST
FETCH NEXT 50 ROWS ONLY) AS "t7"