SELECT COALESCE("t17"."USERID", "t17"."USERID") AS "USERID", "t17"."DISPLAYNAME", "t17"."BADGECOUNT", "t17"."QUESTIONCOUNT", "t17"."ANSWERCOUNT", "t17"."TOTALVIEWS", "t17"."AVGSCORE", "t17"."PERFORMANCESCORE"
FROM (SELECT "t15"."USERID", "t15"."DISPLAYNAME", "t15"."BADGECOUNT", "t15"."QUESTIONCOUNT", "t15"."ANSWERCOUNT", "t15"."TOTALVIEWS", "t15"."AVGSCORE", "t8"."PERFORMANCESCORE"
FROM (SELECT "USERID", "DISPLAYNAME", "PERFORMANCESCORE"
FROM "s1"
ORDER BY "PERFORMANCESCORE" DESC NULLS FIRST
FETCH NEXT 10 ROWS ONLY) AS "t8"
INNER JOIN (SELECT "Users1"."Id" AS "USERID", "Users1"."DisplayName" AS "DISPLAYNAME", CASE WHEN "t11"."TOTALBADGES" IS NOT NULL THEN CAST("t11"."TOTALBADGES" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t13"."QUESTIONCOUNT" IS NOT NULL THEN CAST("t13"."QUESTIONCOUNT" AS BIGINT) ELSE 0 END AS "QUESTIONCOUNT", CASE WHEN "t13"."ANSWERCOUNT" IS NOT NULL THEN CAST("t13"."ANSWERCOUNT" AS BIGINT) ELSE 0 END AS "ANSWERCOUNT", CASE WHEN "t13"."$f3" IS NOT NULL THEN CAST("t13"."$f3" AS INTEGER) ELSE 0 END AS "TOTALVIEWS", CASE WHEN "t13"."$f4" IS NOT NULL THEN CAST("t13"."$f4" AS INTEGER) ELSE 0 END AS "AVGSCORE"
FROM "STACK"."Users" AS "Users1"
LEFT JOIN (SELECT ANY_VALUE("Users2"."Id") AS "USERID", "Users2"."DisplayName" AS "DISPLAYNAME", COUNT("Badges0"."Id") AS "TOTALBADGES", SUM(CASE WHEN CAST("Badges0"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges0"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges0"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges" AS "Badges0"
RIGHT JOIN "STACK"."Users" AS "Users2" ON "Badges0"."UserId" = "Users2"."Id"
GROUP BY "Users2"."Id", "Users2"."DisplayName") AS "t11" ON "Users1"."Id" = "t11"."USERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "QUESTIONCOUNT", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "ANSWERCOUNT", SUM("ViewCount") AS "$f3", AVG("Score") AS "$f4"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t13" ON "Users1"."Id" = "t13"."OWNERUSERID"
WHERE CASE WHEN "t13"."QUESTIONCOUNT" IS NOT NULL THEN CAST("t13"."QUESTIONCOUNT" AS BIGINT) ELSE 0 END > 10 AND (CASE WHEN "t13"."ANSWERCOUNT" IS NOT NULL THEN CAST("t13"."ANSWERCOUNT" AS BIGINT) ELSE 0 END > 5 OR CASE WHEN "t11"."TOTALBADGES" IS NOT NULL THEN CAST("t11"."TOTALBADGES" AS BIGINT) ELSE 0 END > 5)) AS "t15" ON "t8"."USERID" = "t15"."USERID"
ORDER BY "t15"."AVGSCORE" DESC NULLS FIRST, "t15"."TOTALVIEWS" DESC NULLS FIRST) AS "t17"