SELECT COALESCE("t8"."USERID", "t8"."USERID") AS "USERID", "t8"."DISPLAYNAME", "t8"."REPUTATION", "t8"."TOTALVOTES", "t8"."UPVOTES", "t8"."DOWNVOTES", "t8"."TOTALBADGES", "t8"."GOLDBADGES", "t8"."SILVERBADGES", "t8"."BRONZEBADGES", "t8"."TOTALPOSTS", "t8"."QUESTIONS", "t8"."ANSWERS", "t8"."TOTALVIEWS", "t8"."TOTALSCORE"
FROM (SELECT "s1"."Id" AS "USERID", "s1"."DisplayName" AS "DISPLAYNAME", "s1"."Reputation" AS "REPUTATION", CASE WHEN "s1"."TOTALVOTES" IS NOT NULL THEN CAST("s1"."TOTALVOTES" AS BIGINT) ELSE 0 END AS "TOTALVOTES", CASE WHEN "s1"."UPVOTES_" IS NOT NULL THEN CAST("s1"."UPVOTES_" AS INTEGER) ELSE 0 END AS "UPVOTES", CASE WHEN "s1"."DOWNVOTES_" IS NOT NULL THEN CAST("s1"."DOWNVOTES_" AS INTEGER) ELSE 0 END AS "DOWNVOTES", CASE WHEN "t4"."TOTALBADGES" IS NOT NULL THEN CAST("t4"."TOTALBADGES" AS BIGINT) ELSE 0 END AS "TOTALBADGES", CASE WHEN "t4"."GOLDBADGES" IS NOT NULL THEN CAST("t4"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t4"."SILVERBADGES" IS NOT NULL THEN CAST("t4"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t4"."BRONZEBADGES" IS NOT NULL THEN CAST("t4"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN "t6"."TOTALPOSTS" IS NOT NULL THEN CAST("t6"."TOTALPOSTS" AS BIGINT) ELSE 0 END AS "TOTALPOSTS", CASE WHEN "t6"."QUESTIONS" IS NOT NULL THEN CAST("t6"."QUESTIONS" AS INTEGER) ELSE 0 END AS "QUESTIONS", CASE WHEN "t6"."ANSWERS" IS NOT NULL THEN CAST("t6"."ANSWERS" AS INTEGER) ELSE 0 END AS "ANSWERS", CASE WHEN "t6"."TOTALVIEWS" IS NOT NULL THEN CAST("t6"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS "TOTALVIEWS", CASE WHEN "t6"."TOTALSCORE" IS NOT NULL THEN CAST("t6"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE"
FROM "s1"
LEFT JOIN (SELECT "UserId" AS "USERID", COUNT(*) AS "TOTALBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
GROUP BY "UserId") AS "t4" ON "s1"."Id" = "t4"."USERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "QUESTIONS", SUM(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "ANSWERS", SUM("ViewCount") AS "TOTALVIEWS", SUM("Score") AS "TOTALSCORE"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t6" ON "s1"."Id" = "t6"."OWNERUSERID"
ORDER BY "s1"."Reputation" DESC NULLS FIRST
FETCH NEXT 50 ROWS ONLY) AS "t8"