SELECT COALESCE("t7"."USERID", "t7"."USERID") AS "USERID", "t7"."DISPLAYNAME", "t7"."TOTALPOSTS", "t7"."TOTALQUESTIONS", "t7"."TOTALANSWERS", "t7"."TOTALSCORE", "t7"."AVGVIEWS", "t7"."TOTALCOMMENTS", "t7"."LASTCOMMENTDATE", "t7"."LASTPOSTDATE"
FROM (SELECT "t5"."USERID", "t5"."DISPLAYNAME", "t5"."TOTALPOSTS", "t5"."TOTALQUESTIONS", "t5"."TOTALANSWERS", "t5"."TOTALSCORE", "t5"."AVGVIEWS", "s1"."TOTALCOMMENTS", "s1"."LASTCOMMENTDATE", "t5"."LASTPOSTDATE"
FROM "s1"
RIGHT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT(DISTINCT "Posts"."Id") AS "TOTALPOSTS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "TOTALQUESTIONS", SUM(CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "TOTALANSWERS", SUM("Posts"."Score") AS "TOTALSCORE", AVG("Posts"."ViewCount") AS "AVGVIEWS", MAX("Posts"."CreationDate") AS "LASTPOSTDATE"
FROM "STACK"."Posts"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Posts"."OwnerUserId" = "Users0"."Id"
GROUP BY "Users0"."Id", "Users0"."DisplayName"
HAVING COUNT(DISTINCT "Posts"."Id") > 5 AND SUM("Posts"."Score") > 100) AS "t5" ON "s1"."USERID" = "t5"."USERID"
ORDER BY "t5"."TOTALSCORE" DESC NULLS FIRST, "t5"."LASTPOSTDATE" DESC NULLS FIRST) AS "t7"