SELECT COALESCE("POSTID", "POSTID") AS "POSTID", "TITLE", "POSTCREATIONDATE", "VIEWCOUNT", "SCORE", "USERID", "USERDISPLAYNAME", "USERREPUTATION", "VOTECOUNT", "UPVOTES", "DOWNVOTES", "COMMENTCOUNT", "BADGECOUNT"
FROM (SELECT ANY_VALUE("Id") AS "POSTID", "TITLE", ANY_VALUE("CreationDate") AS "POSTCREATIONDATE", "VIEWCOUNT", "SCORE", ANY_VALUE("Id0") AS "USERID", ANY_VALUE("DisplayName") AS "USERDISPLAYNAME", ANY_VALUE("Reputation") AS "USERREPUTATION", COUNT("Id1") AS "VOTECOUNT", SUM("FD_COL_9") AS "UPVOTES", SUM("FD_COL_10") AS "DOWNVOTES", COUNT("Id2") AS "COMMENTCOUNT", COUNT("Id3") AS "BADGECOUNT"
FROM "s1"
GROUP BY "Id", "TITLE", "CreationDate", "VIEWCOUNT", "SCORE", "Id0", "DisplayName", "Reputation"
ORDER BY "SCORE" DESC NULLS FIRST, "VIEWCOUNT" DESC NULLS FIRST) AS "t6"