SELECT COALESCE("t7"."POSTID", "t7"."POSTID") AS "POSTID", "t7"."TITLE", "t7"."CREATIONDATE", "t7"."SCORE", "t7"."VIEWCOUNT", "t7"."OWNERREPUTATION", "t7"."OWNERDISPLAYNAME", "t7"."UPVOTECOUNT", "t7"."DOWNVOTECOUNT", "t7"."TOTALCOMMENTS", "t7"."ANSWERCOUNT", "t7"."COMMENTCOUNT", "t7"."FAVORITECOUNT", "t7"."LASTACTIVITYDATE"
FROM (SELECT "t5"."POSTID", "t5"."TITLE", "t5"."CREATIONDATE", "t5"."SCORE", "t5"."VIEWCOUNT", "t5"."OWNERREPUTATION", "t5"."OWNERDISPLAYNAME", CASE WHEN "t5"."UPVOTECOUNT" IS NOT NULL THEN CAST("t5"."UPVOTECOUNT" AS BIGINT) ELSE 0 END AS "UPVOTECOUNT", CASE WHEN "t5"."DOWNVOTECOUNT" IS NOT NULL THEN CAST("t5"."DOWNVOTECOUNT" AS BIGINT) ELSE 0 END AS "DOWNVOTECOUNT", CASE WHEN "t1"."TOTALCOMMENTS" IS NOT NULL THEN CAST("t1"."TOTALCOMMENTS" AS BIGINT) ELSE 0 END AS "TOTALCOMMENTS", "t5"."ANSWERCOUNT", "t5"."COMMENTCOUNT", "t5"."FAVORITECOUNT", "t5"."LASTACTIVITYDATE"
FROM (SELECT "PostId", COUNT(*) AS "TOTALCOMMENTS"
FROM "STACK"."Comments"
GROUP BY "PostId") AS "t1"
RIGHT JOIN (SELECT "t4"."POSTID", "t4"."TITLE", "t4"."CREATIONDATE", "t4"."SCORE", "t4"."VIEWCOUNT", "t4"."OWNERUSERID", "t4"."ANSWERCOUNT", "t4"."COMMENTCOUNT", "t4"."FAVORITECOUNT", "t4"."LASTACTIVITYDATE", "t4"."OWNERREPUTATION", "t4"."OWNERDISPLAYNAME", "t3"."POSTID" AS "POSTID0", "t3"."UPVOTECOUNT", "t3"."DOWNVOTECOUNT"
FROM (SELECT "PostId" AS "POSTID", COUNT(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "UPVOTECOUNT", COUNT(CASE WHEN CAST("VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE NULL END) AS "DOWNVOTECOUNT"
FROM "STACK"."Votes"
GROUP BY "PostId") AS "t3"
RIGHT JOIN (SELECT "s1"."Id" AS "POSTID", "s1"."Title" AS "TITLE", "s1"."CreationDate" AS "CREATIONDATE", "s1"."Score" AS "SCORE", "s1"."ViewCount" AS "VIEWCOUNT", "s1"."OwnerUserId" AS "OWNERUSERID", "s1"."AnswerCount" AS "ANSWERCOUNT", "s1"."CommentCount" AS "COMMENTCOUNT", "s1"."FavoriteCount" AS "FAVORITECOUNT", "s1"."LastActivityDate" AS "LASTACTIVITYDATE", "Users"."Reputation" AS "OWNERREPUTATION", "Users"."DisplayName" AS "OWNERDISPLAYNAME"
FROM "s1"
INNER JOIN "STACK"."Users" ON "s1"."OwnerUserId" = "Users"."Id") AS "t4" ON "t3"."POSTID" = "t4"."POSTID") AS "t5" ON "t1"."PostId" = "t5"."POSTID"
ORDER BY "t5"."CREATIONDATE" DESC NULLS FIRST) AS "t7"