SELECT COALESCE("t0"."POSTTYPE", "t0"."POSTTYPE") AS "POSTTYPE", "t0"."TOTALPOSTS", "t0"."AVERAGESCORE", "t0"."UNIQUEUSERS", "t0"."TOTALVIEWS", "t0"."TOTALCOMMENTS", "t0"."TOTALANSWERS", "t3"."USERID", "t3"."DISPLAYNAME", "t3"."POSTSCREATED", "t3"."TOTALBOUNTYRECEIVED"
FROM (SELECT ANY_VALUE("PostTypes"."Name") AS "POSTTYPE", COUNT(*) AS "TOTALPOSTS", AVG("Posts"."Score") AS "AVERAGESCORE", COUNT(DISTINCT "Posts"."OwnerUserId") AS "UNIQUEUSERS", SUM("Posts"."ViewCount") AS "TOTALVIEWS", SUM("Posts"."CommentCount") AS "TOTALCOMMENTS", SUM("Posts"."AnswerCount") AS "TOTALANSWERS"
FROM "STACK"."PostTypes"
INNER JOIN "STACK"."Posts" ON "PostTypes"."Id" = "Posts"."PostTypeId"
GROUP BY "PostTypes"."Name") AS "t0"
INNER JOIN (SELECT ANY_VALUE("t1"."Id") AS "USERID", "t1"."DisplayName" AS "DISPLAYNAME", COUNT("t1"."Id0") AS "POSTSCREATED", SUM("Votes"."BountyAmount") AS "TOTALBOUNTYRECEIVED"
FROM "STACK"."Votes"
RIGHT JOIN (SELECT "Users"."Id", "Users"."Reputation", "Users"."CreationDate", "Users"."DisplayName", "Users"."LastAccessDate", "Users"."WebsiteUrl", "Users"."Location", "Users"."AboutMe", "Users"."Views", "Users"."UpVotes", "Users"."DownVotes", "Users"."ProfileImageUrl", "Users"."AccountId", "Posts0"."Id" AS "Id0", "Posts0"."PostTypeId", "Posts0"."AcceptedAnswerId", "Posts0"."ParentId", "Posts0"."CreationDate" AS "CreationDate0", "Posts0"."Score", "Posts0"."ViewCount", "Posts0"."Body", "Posts0"."OwnerUserId", "Posts0"."OwnerDisplayName", "Posts0"."LastEditorUserId", "Posts0"."LastEditorDisplayName", "Posts0"."LastEditDate", "Posts0"."LastActivityDate", "Posts0"."Title", "Posts0"."Tags", "Posts0"."AnswerCount", "Posts0"."CommentCount", "Posts0"."FavoriteCount", "Posts0"."ClosedDate", "Posts0"."CommunityOwnedDate", "Posts0"."ContentLicense"
FROM "STACK"."Posts" AS "Posts0"
RIGHT JOIN "STACK"."Users" ON "Posts0"."OwnerUserId" = "Users"."Id") AS "t1" ON "Votes"."UserId" = "t1"."Id"
GROUP BY "t1"."Id", "t1"."DisplayName") AS "t3" ON "t0"."UNIQUEUSERS" = "t3"."POSTSCREATED"