SELECT COALESCE("t"."Id", "t"."Id") AS "Id", "t"."Title", "t"."CreationDate", "t"."DisplayName", "t"."Name", ANY_VALUE("t"."Id") AS "POSTID", ANY_VALUE("t"."DisplayName") AS "OWNERDISPLAYNAME", ANY_VALUE("t"."Name") AS "POSTTYPE", COUNT("t"."Id2") AS "COMMENTCOUNT", COUNT("Votes"."Id") AS "VOTECOUNT"
FROM (SELECT "Posts"."Id", "Posts"."PostTypeId", "Posts"."AcceptedAnswerId", "Posts"."ParentId", "Posts"."CreationDate", "Posts"."Score", "Posts"."ViewCount", "Posts"."Body", "Posts"."OwnerUserId", "Posts"."OwnerDisplayName", "Posts"."LastEditorUserId", "Posts"."LastEditorDisplayName", "Posts"."LastEditDate", "Posts"."LastActivityDate", "Posts"."Title", "Posts"."Tags", "Posts"."AnswerCount", "Posts"."CommentCount", "Posts"."FavoriteCount", "Posts"."ClosedDate", "Posts"."CommunityOwnedDate", "Posts"."ContentLicense", "Users"."Id" AS "Id0", "Users"."Reputation", "Users"."CreationDate" AS "CreationDate0", "Users"."DisplayName", "Users"."LastAccessDate", "Users"."WebsiteUrl", "Users"."Location", "Users"."AboutMe", "Users"."Views", "Users"."UpVotes", "Users"."DownVotes", "Users"."ProfileImageUrl", "Users"."AccountId", "PostTypes"."Id" AS "Id1", "PostTypes"."Name", "Comments"."Id" AS "Id2", "Comments"."PostId", "Comments"."Score" AS "Score0", "Comments"."Text", "Comments"."CreationDate" AS "CreationDate1", "Comments"."UserDisplayName", "Comments"."UserId", "Comments"."ContentLicense" AS "ContentLicense0"
FROM "STACK"."Comments"
RIGHT JOIN ("STACK"."Posts" INNER JOIN "STACK"."PostTypes" ON "Posts"."PostTypeId" = "PostTypes"."Id" INNER JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id") ON "Comments"."PostId" = "Posts"."Id") AS "t"
LEFT JOIN "STACK"."Votes" ON "t"."Id" = "Votes"."PostId"
GROUP BY "t"."Id", "t"."CreationDate", "t"."Title", "t"."DisplayName", "t"."Name"