SELECT COALESCE("POSTID", "POSTID") AS "POSTID", "TITLE", "CREATIONDATE", "SCORE", "VIEWCOUNT", "OWNERDISPLAYNAME", "COMMENTCOUNT", "UPVOTES", "DOWNVOTES", "CLOSERATE", "REOPENRATE"
FROM (SELECT ANY_VALUE("Id") AS "POSTID", "TITLE", "CREATIONDATE", "SCORE", "VIEWCOUNT", ANY_VALUE("DisplayName") AS "OWNERDISPLAYNAME", COUNT("Id1") AS "COMMENTCOUNT", SUM("FD_COL_7") AS "UPVOTES", SUM("FD_COL_8") AS "DOWNVOTES", AVG("FD_COL_9") AS "CLOSERATE", AVG("FD_COL_10") AS "REOPENRATE"
FROM "s1"
GROUP BY "Id", "TITLE", "CREATIONDATE", "SCORE", "VIEWCOUNT", "DisplayName"
ORDER BY "CREATIONDATE" DESC NULLS FIRST) AS "t7"