SELECT COALESCE("POSTID", "POSTID") AS "POSTID", "TITLE", "CREATIONDATE", "SCORE", "VIEWCOUNT", "OWNERDISPLAYNAME", "OWNERREPUTATION", "ANSWERCOUNT", "COMMENTCOUNT", "LINKSCOUNT", "HISTORYCOUNT"
FROM (SELECT "Id" AS "POSTID", "Title" AS "TITLE", "CreationDate" AS "CREATIONDATE", "Score" AS "SCORE", "ViewCount" AS "VIEWCOUNT", "DisplayName" AS "OWNERDISPLAYNAME", "Reputation" AS "OWNERREPUTATION", "AnswerCount" AS "ANSWERCOUNT", CASE WHEN "COMMENTCOUNT_" IS NOT NULL THEN CAST("COMMENTCOUNT_" AS BIGINT) ELSE 0 END AS "COMMENTCOUNT", CASE WHEN "LINKSCOUNT" IS NOT NULL THEN CAST("LINKSCOUNT" AS BIGINT) ELSE 0 END AS "LINKSCOUNT", CASE WHEN "HISTORYCOUNT" IS NOT NULL THEN CAST("HISTORYCOUNT" AS BIGINT) ELSE 0 END AS "HISTORYCOUNT"
FROM "s1"
ORDER BY "Score" DESC NULLS FIRST, "CreationDate" DESC NULLS FIRST
FETCH NEXT 100 ROWS ONLY) AS "t6"