SELECT COALESCE("POSTID", "POSTID") AS "POSTID", "TITLE", "CREATIONDATE", "SCORE", "VIEWCOUNT", "ANSWERCOUNT", "COMMENTCOUNT", "OWNERDISPLAYNAME", "OWNERREPUTATION", "POSTHISTORYTYPEID", "POSTHISTORYDATE", "VOTECOUNT", "UPVOTES", "DOWNVOTES", "BADGECOUNT"
FROM (SELECT ANY_VALUE("Id") AS "POSTID", "TITLE", "CREATIONDATE", "SCORE", "VIEWCOUNT", "ANSWERCOUNT", "COMMENTCOUNT", ANY_VALUE("DisplayName") AS "OWNERDISPLAYNAME", ANY_VALUE("Reputation") AS "OWNERREPUTATION", "POSTHISTORYTYPEID", ANY_VALUE("CreationDate1") AS "POSTHISTORYDATE", COUNT("Id2") AS "VOTECOUNT", SUM("FD_COL_13") AS "UPVOTES", SUM("FD_COL_14") AS "DOWNVOTES", SUM("FD_COL_15") AS "BADGECOUNT"
FROM "s1"
GROUP BY "Id", "TITLE", "CREATIONDATE", "SCORE", "VIEWCOUNT", "ANSWERCOUNT", "COMMENTCOUNT", "Id0", "DisplayName", "Reputation", "POSTHISTORYTYPEID", "CreationDate1"
ORDER BY "CREATIONDATE" DESC NULLS FIRST) AS "t6"