SELECT COALESCE("t7"."USERID", "t7"."USERID") AS "USERID", "t7"."DISPLAYNAME", "t7"."POSTCOUNT", "t7"."QUESTIONCOUNT", "t7"."ANSWERCOUNT", "t7"."TOTALVIEWS", "t7"."AVERAGESCORE", "t7"."POSTTYPE", "t7"."TOTALPOSTS", "t7"."AVERAGEVIEWS", "t7"."AVERAGESCORE0"
FROM (SELECT "t5"."USERID", "t5"."DISPLAYNAME", "t5"."POSTCOUNT", "t5"."QUESTIONCOUNT", "t5"."ANSWERCOUNT", "t5"."TOTALVIEWS", "t5"."AVERAGESCORE", "t2"."POSTTYPE", "t2"."TOTALPOSTS", "t2"."AVERAGEVIEWS", "t2"."AVERAGESCORE" AS "AVERAGESCORE0"
FROM (SELECT ANY_VALUE("PostTypes"."Name") AS "POSTTYPE", COUNT(*) AS "TOTALPOSTS", AVG("Posts0"."ViewCount") AS "AVERAGEVIEWS", AVG("Posts0"."Score") AS "AVERAGESCORE"
FROM "STACK"."PostTypes"
INNER JOIN "STACK"."Posts" AS "Posts0" ON "PostTypes"."Id" = "Posts0"."PostTypeId"
GROUP BY "PostTypes"."Name") AS "t2",
(SELECT ANY_VALUE("Id") AS "USERID", "DISPLAYNAME", COUNT("Id0") AS "POSTCOUNT", SUM("FD_COL_3") AS "QUESTIONCOUNT", SUM("FD_COL_4") AS "ANSWERCOUNT", SUM("ViewCount") AS "TOTALVIEWS", AVG("Score") AS "AVERAGESCORE", COUNT("Id0") > 0 AS "$f7"
FROM "s1"
GROUP BY "Id", "DISPLAYNAME"
HAVING COUNT("Id0") > 0) AS "t5"
ORDER BY "t5"."TOTALVIEWS" DESC NULLS FIRST, "t5"."AVERAGESCORE" DESC NULLS FIRST) AS "t7"