SELECT COALESCE("t9"."TITLE", "t9"."TITLE") AS "TITLE", "t9"."VIEWCOUNT", "t9"."SCORE", "t9"."COMMENTCOUNT", "t9"."VOTECOUNT", "t9"."UPVOTECOUNT", "t9"."DOWNVOTECOUNT", "t9"."OWNERDISPLAYNAME", "t9"."POSTCOUNT", "t9"."TOTALUPVOTES", "t9"."TOTALDOWNVOTES", "t9"."AVERAGEREPUTATION"
FROM (SELECT "t7"."TITLE", "t7"."VIEWCOUNT", "t7"."SCORE", "t7"."COMMENTCOUNT", "t7"."VOTECOUNT", "t7"."UPVOTECOUNT", "t7"."DOWNVOTECOUNT", "t5"."DISPLAYNAME" AS "OWNERDISPLAYNAME", "t5"."POSTCOUNT", "t5"."TOTALUPVOTES", "t5"."TOTALDOWNVOTES", "t5"."AVERAGEREPUTATION"
FROM (SELECT ANY_VALUE("Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", COUNT(DISTINCT "Posts0"."Id") AS "POSTCOUNT", SUM("Users"."UpVotes") AS "TOTALUPVOTES", SUM("Users"."DownVotes") AS "TOTALDOWNVOTES", AVG("Users"."Reputation") AS "AVERAGEREPUTATION"
FROM "STACK"."Posts" AS "Posts0"
RIGHT JOIN "STACK"."Users" ON "Posts0"."OwnerUserId" = "Users"."Id"
GROUP BY "Users"."Id", "Users"."DisplayName") AS "t5"
INNER JOIN (SELECT ANY_VALUE("Id") AS "POSTID", "TITLE", "CREATIONDATE", "VIEWCOUNT", "SCORE", COUNT("Id0") AS "COMMENTCOUNT", COUNT(DISTINCT "Id1") AS "VOTECOUNT", SUM("FD_COL_7") AS "UPVOTECOUNT", SUM("FD_COL_8") AS "DOWNVOTECOUNT", COUNT(DISTINCT "Id2") AS "BADGECOUNT"
FROM "s1"
GROUP BY "Id", "TITLE", "CREATIONDATE", "VIEWCOUNT", "SCORE") AS "t7" ON "t5"."USERID" = "t7"."POSTID"
ORDER BY "t7"."VIEWCOUNT" DESC NULLS FIRST
FETCH NEXT 100 ROWS ONLY) AS "t9"