SELECT COALESCE("POSTTYPEID", "POSTTYPEID") AS "POSTTYPEID", "POSTTYPENAME", "TOTALPOSTS", "TOTALSCORE", "TOTALVIEWCOUNT", "AVGVIEWCOUNT", "TOTALQUESTIONS", "TOTALANSWERS", "TOTALWIKIS", "UNIQUEUSERS", "TOTALREPUTATION", "TOTALCOMMENTS", "TOTALVOTES"
FROM (SELECT "POSTTYPEID", "POSTTYPENAME", "TOTALPOSTS", "TOTALSCORE", "TOTALVIEWCOUNT", "AVGVIEWCOUNT", CASE WHEN "FD_COL_7" IS NOT NULL THEN CAST("FD_COL_7" AS INTEGER) ELSE 0 END AS "TOTALQUESTIONS", CASE WHEN "FD_COL_8" IS NOT NULL THEN CAST("FD_COL_8" AS INTEGER) ELSE 0 END AS "TOTALANSWERS", CASE WHEN "FD_COL_9" IS NOT NULL THEN CAST("FD_COL_9" AS INTEGER) ELSE 0 END AS "TOTALWIKIS", "UNIQUEUSERS", "TOTALREPUTATION", "TOTALCOMMENTS", "TOTALVOTES"
FROM "s1"
ORDER BY "POSTTYPEID") AS "t3"