SELECT COALESCE("t1"."DisplayName", "t1"."DisplayName") AS "DisplayName", "t1"."Reputation" AS "REPUTATION", ANY_VALUE("t1"."DisplayName") AS "USERDISPLAYNAME", COUNT("t1"."Id0") AS "TOTALPOSTS", SUM(CASE WHEN CAST("t1"."PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "QUESTIONSCOUNT", SUM(CASE WHEN CAST("t1"."PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "ANSWERSCOUNT", SUM("t1"."ViewCount") AS "TOTALVIEWS", SUM("t1"."Score") AS "TOTALSCORE", AVG(CASE WHEN LENGTH("t1"."Body") IS NOT NULL THEN CAST(LENGTH("t1"."Body") AS INTEGER) ELSE 0 END) AS "AVERAGEPOSTLENGTH", COUNT("t1"."Id1") AS "TOTALCOMMENTS", COUNT("Badges"."Id") AS "TOTALBADGES"
FROM (SELECT "t0"."Id", "t0"."Reputation", "t0"."CreationDate", "t0"."DisplayName", "t0"."LastAccessDate", "t0"."WebsiteUrl", "t0"."Location", "t0"."AboutMe", "t0"."Views", "t0"."UpVotes", "t0"."DownVotes", "t0"."ProfileImageUrl", "t0"."AccountId", "t0"."Id0", "t0"."PostTypeId", "t0"."AcceptedAnswerId", "t0"."ParentId", "t0"."CreationDate0", "t0"."Score", "t0"."ViewCount", "t0"."Body", "t0"."OwnerUserId", "t0"."OwnerDisplayName", "t0"."LastEditorUserId", "t0"."LastEditorDisplayName", "t0"."LastEditDate", "t0"."LastActivityDate", "t0"."Title", "t0"."Tags", "t0"."AnswerCount", "t0"."CommentCount", "t0"."FavoriteCount", "t0"."ClosedDate", "t0"."CommunityOwnedDate", "t0"."ContentLicense", "Comments"."Id" AS "Id1", "Comments"."PostId", "Comments"."Score" AS "Score0", "Comments"."Text", "Comments"."CreationDate" AS "CreationDate1", "Comments"."UserDisplayName", "Comments"."UserId", "Comments"."ContentLicense" AS "ContentLicense0"
