SELECT COALESCE("POSTID", "POSTID") AS "POSTID", "TITLE", "CREATIONDATE", "VIEWCOUNT", "SCORE", "OWNERDISPLAYNAME", "COMMENTCOUNT", "ANSWERCOUNT", "VOTECOUNT"
FROM (SELECT ANY_VALUE("Id") AS "POSTID", "TITLE", "CREATIONDATE", "VIEWCOUNT", "SCORE", ANY_VALUE("DisplayName") AS "OWNERDISPLAYNAME", COUNT("FD_COL_6") AS "COMMENTCOUNT", COUNT(DISTINCT "Id2") AS "ANSWERCOUNT", COUNT(DISTINCT "Id3") AS "VOTECOUNT"
FROM "s1"
GROUP BY "Id", "TITLE", "CREATIONDATE", "VIEWCOUNT", "SCORE", "DisplayName"
ORDER BY "CREATIONDATE" DESC NULLS FIRST) AS "t6"