SELECT COALESCE("t10"."POSTID", "t10"."POSTID") AS "POSTID", "t10"."TITLE", "t10"."CREATIONDATE", "t10"."VIEWCOUNT", "t10"."SCORE", "t10"."COMMENTCOUNT", "t10"."VOTECOUNT", "t10"."UPVOTES", "t10"."DOWNVOTES", "t10"."OWNERDISPLAYNAME", "t10"."USERREPUTATION", "t10"."TOTALPOSTS", "t10"."TOTALBADGES"
FROM (SELECT "t8"."POSTID", "t8"."TITLE", "t8"."CREATIONDATE", "t8"."VIEWCOUNT", "t8"."SCORE", "t8"."COMMENTCOUNT", "t8"."VOTECOUNT", "t8"."UPVOTES", "t8"."DOWNVOTES", "t4"."DISPLAYNAME" AS "OWNERDISPLAYNAME", "t4"."REPUTATION" AS "USERREPUTATION", "t4"."POSTSCOUNT" AS "TOTALPOSTS", "t4"."BADGESCOUNT" AS "TOTALBADGES"
FROM (SELECT ANY_VALUE("t1"."Id") AS "USERID", "t1"."DisplayName" AS "DISPLAYNAME", "t1"."Reputation" AS "REPUTATION", COUNT(DISTINCT "t1"."Id0") AS "POSTSCOUNT", SUM("Badges"."Class") AS "BADGESCOUNT"
FROM (SELECT "Users"."Id", "Users"."Reputation", "Users"."CreationDate", "Users"."DisplayName", "Users"."LastAccessDate", "Users"."WebsiteUrl", "Users"."Location", "Users"."AboutMe", "Users"."Views", "Users"."UpVotes", "Users"."DownVotes", "Users"."ProfileImageUrl", "Users"."AccountId", "Posts0"."Id" AS "Id0", "Posts0"."PostTypeId", "Posts0"."AcceptedAnswerId", "Posts0"."ParentId", "Posts0"."CreationDate" AS "CreationDate0", "Posts0"."Score", "Posts0"."ViewCount", "Posts0"."Body", "Posts0"."OwnerUserId", "Posts0"."OwnerDisplayName", "Posts0"."LastEditorUserId", "Posts0"."LastEditorDisplayName", "Posts0"."LastEditDate", "Posts0"."LastActivityDate", "Posts0"."Title", "Posts0"."Tags", "Posts0"."AnswerCount", "Posts0"."CommentCount", "Posts0"."FavoriteCount", "Posts0"."ClosedDate", "Posts0"."CommunityOwnedDate", "Posts0"."ContentLicense"
FROM "STACK"."Posts" AS "Posts0"
RIGHT JOIN "STACK"."Users" ON "Posts0"."OwnerUserId" = "Users"."Id") AS "t1"
LEFT JOIN "STACK"."Badges" ON "t1"."Id" = "Badges"."UserId"
GROUP BY "t1"."Id", "t1"."Reputation", "t1"."DisplayName") AS "t4"
INNER JOIN (SELECT ANY_VALUE("t5"."Id") AS "POSTID", "t5"."Title" AS "TITLE", "t5"."CreationDate" AS "CREATIONDATE", "t5"."ViewCount" AS "VIEWCOUNT", "t5"."Score" AS "SCORE", COUNT("t5"."Id0") AS "COMMENTCOUNT", COUNT("Votes"."Id") AS "VOTECOUNT", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES", "t5"."OwnerUserId" AS "OWNERUSERID"
FROM "STACK"."Votes"
RIGHT JOIN (SELECT "s1"."Id", "s1"."PostTypeId", "s1"."AcceptedAnswerId", "s1"."ParentId", "s1"."CreationDate", "s1"."Score", "s1"."ViewCount", "s1"."Body", "s1"."OwnerUserId", "s1"."OwnerDisplayName", "s1"."LastEditorUserId", "s1"."LastEditorDisplayName", "s1"."LastEditDate", "s1"."LastActivityDate", "s1"."Title", "s1"."Tags", "s1"."AnswerCount", "s1"."CommentCount", "s1"."FavoriteCount", "s1"."ClosedDate", "s1"."CommunityOwnedDate", "s1"."ContentLicense", "Comments"."Id" AS "Id0", "Comments"."PostId", "Comments"."Score" AS "Score0", "Comments"."Text", "Comments"."CreationDate" AS "CreationDate0", "Comments"."UserDisplayName", "Comments"."UserId", "Comments"."ContentLicense" AS "ContentLicense0"
FROM "STACK"."Comments"
RIGHT JOIN "s1" ON "Comments"."PostId" = "s1"."Id") AS "t5" ON "Votes"."PostId" = "t5"."Id"
GROUP BY "t5"."Id", "t5"."Title", "t5"."CreationDate", "t5"."ViewCount", "t5"."Score", "t5"."OwnerUserId") AS "t8" ON "t4"."USERID" = "t8"."OWNERUSERID"
ORDER BY "t8"."SCORE" DESC NULLS FIRST, "t8"."VIEWCOUNT" DESC NULLS FIRST) AS "t10"