SELECT COALESCE("t"."Id", "t"."Id") AS "Id", "t"."Title" AS "TITLE", "t"."CreationDate", "t"."Score" AS "SCORE", "t"."ViewCount" AS "VIEWCOUNT", "t"."DisplayName", "t"."Reputation", "Tags"."TagName", "PostTypes"."Name", ANY_VALUE("t"."Id") AS "POSTID", ANY_VALUE("t"."CreationDate") AS "POSTCREATIONDATE", ANY_VALUE("t"."DisplayName") AS "OWNERDISPLAYNAME", ANY_VALUE("t"."Reputation") AS "OWNERREPUTATION", COUNT("t"."Id1") AS "COMMENTCOUNT", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTECOUNT", SUM(CASE WHEN CAST("Votes"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTECOUNT", ANY_VALUE("Tags"."TagName") AS "POSTTAG", ANY_VALUE("PostTypes"."Name") AS "POSTTYPENAME"
FROM "STACK"."PostTypes"
INNER JOIN ((SELECT "Posts"."Id", "Posts"."PostTypeId", "Posts"."AcceptedAnswerId", "Posts"."ParentId", "Posts"."CreationDate", "Posts"."Score", "Posts"."ViewCount", "Posts"."Body", "Posts"."OwnerUserId", "Posts"."OwnerDisplayName", "Posts"."LastEditorUserId", "Posts"."LastEditorDisplayName", "Posts"."LastEditDate", "Posts"."LastActivityDate", "Posts"."Title", "Posts"."Tags", "Posts"."AnswerCount", "Posts"."CommentCount", "Posts"."FavoriteCount", "Posts"."ClosedDate", "Posts"."CommunityOwnedDate", "Posts"."ContentLicense", "Users"."Id" AS "Id0", "Users"."Reputation", "Users"."CreationDate" AS "CreationDate0", "Users"."DisplayName", "Users"."LastAccessDate", "Users"."WebsiteUrl", "Users"."Location", "Users"."AboutMe", "Users"."Views", "Users"."UpVotes", "Users"."DownVotes", "Users"."ProfileImageUrl", "Users"."AccountId", "Comments"."Id" AS "Id1", "Comments"."PostId", "Comments"."Score" AS "Score0", "Comments"."Text", "Comments"."CreationDate" AS "CreationDate1", "Comments"."UserDisplayName", "Comments"."UserId", "Comments"."ContentLicense" AS "ContentLicense0"
FROM "STACK"."Comments"
RIGHT JOIN ("STACK"."Posts" INNER JOIN "STACK"."Users" ON "Posts"."OwnerUserId" = "Users"."Id") ON "Comments"."PostId" = "Posts"."Id") AS "t" LEFT JOIN "STACK"."Votes" ON "t"."Id" = "Votes"."PostId" LEFT JOIN "STACK"."Tags" ON "t"."Id" = "Tags"."ExcerptPostId") ON "PostTypes"."Id" = "t"."PostTypeId"
GROUP BY "t"."Id", "t"."Title", "t"."CreationDate", "t"."Score", "t"."ViewCount", "t"."DisplayName", "t"."Reputation", "Tags"."TagName", "PostTypes"."Name"