SELECT COALESCE("Comments"."Id", "Comments"."Id") AS "Id", "Comments"."PostId", "Comments"."Score", "Comments"."Text", "Comments"."CreationDate", "Comments"."UserDisplayName", "Comments"."UserId", "Comments"."ContentLicense", "Posts"."Id" AS "Id0", "Posts"."PostTypeId", "Posts"."AcceptedAnswerId", "Posts"."ParentId", "Posts"."CreationDate" AS "CreationDate0", "Posts"."Score" AS "Score0", "Posts"."ViewCount", "Posts"."Body", "Posts"."OwnerUserId", "Posts"."OwnerDisplayName", "Posts"."LastEditorUserId", "Posts"."LastEditorDisplayName", "Posts"."LastEditDate", "Posts"."LastActivityDate", "Posts"."Title", "Posts"."Tags", "Posts"."AnswerCount", "Posts"."CommentCount", "Posts"."FavoriteCount", "Posts"."ClosedDate", "Posts"."CommunityOwnedDate", "Posts"."ContentLicense" AS "ContentLicense0", "PostTypes"."Id" AS "Id00", "PostTypes"."Name"
FROM "STACK"."Comments"
RIGHT JOIN ("STACK"."Posts" INNER JOIN "STACK"."PostTypes" ON "Posts"."PostTypeId" = "PostTypes"."Id") ON "Comments"."PostId" = "Posts"."Id"