SELECT COALESCE("t7"."POSTID", "t7"."POSTID") AS "POSTID", "t7"."TITLE", "t7"."POSTTYPEID", "t7"."CREATIONDATE", "t7"."VOTECOUNT", "t7"."COMMENTCOUNT", "t7"."BADGECOUNT", "t7"."UPVOTES", "t7"."DOWNVOTES", "t7"."OWNERDISPLAYNAME", "t7"."OWNERREPUTATION"
FROM (SELECT "t5"."POSTID", "t5"."TITLE", "t5"."POSTTYPEID", "t5"."CREATIONDATE", "t5"."VOTECOUNT", "t5"."COMMENTCOUNT", "t5"."BADGECOUNT", "t5"."UPVOTES", "t5"."DOWNVOTES", "Users"."DisplayName" AS "OWNERDISPLAYNAME", "Users"."Reputation" AS "OWNERREPUTATION"
FROM "STACK"."Users"
INNER JOIN (SELECT ANY_VALUE("t2"."Id") AS "POSTID", "t2"."Title" AS "TITLE", "t2"."PostTypeId" AS "POSTTYPEID", "t2"."CreationDate" AS "CREATIONDATE", COUNT("t2"."Id0") AS "VOTECOUNT", COUNT("t2"."Id1") AS "COMMENTCOUNT", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("t2"."VoteTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "UPVOTES", SUM(CASE WHEN CAST("t2"."VoteTypeId" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "DOWNVOTES"
FROM (SELECT "t1"."Id", "t1"."PostTypeId", "t1"."AcceptedAnswerId", "t1"."ParentId", "t1"."CreationDate", "t1"."Score", "t1"."ViewCount", "t1"."Body", "t1"."OwnerUserId", "t1"."OwnerDisplayName", "t1"."LastEditorUserId", "t1"."LastEditorDisplayName", "t1"."LastEditDate", "t1"."LastActivityDate", "t1"."Title", "t1"."Tags", "t1"."AnswerCount", "t1"."CommentCount", "t1"."FavoriteCount", "t1"."ClosedDate", "t1"."CommunityOwnedDate", "t1"."ContentLicense", "t1"."Id0", "t1"."PostId", "t1"."VoteTypeId", "t1"."UserId", "t1"."CreationDate0", "t1"."BountyAmount", "Comments"."Id" AS "Id1", "Comments"."PostId" AS "PostId0", "Comments"."Score" AS "Score0", "Comments"."Text", "Comments"."CreationDate" AS "CreationDate1", "Comments"."UserDisplayName", "Comments"."UserId" AS "UserId0", "Comments"."ContentLicense" AS "ContentLicense0"
FROM "STACK"."Comments"
RIGHT JOIN (SELECT "s1"."Id", "s1"."PostTypeId", "s1"."AcceptedAnswerId", "s1"."ParentId", "s1"."CreationDate", "s1"."Score", "s1"."ViewCount", "s1"."Body", "s1"."OwnerUserId", "s1"."OwnerDisplayName", "s1"."LastEditorUserId", "s1"."LastEditorDisplayName", "s1"."LastEditDate", "s1"."LastActivityDate", "s1"."Title", "s1"."Tags", "s1"."AnswerCount", "s1"."CommentCount", "s1"."FavoriteCount", "s1"."ClosedDate", "s1"."CommunityOwnedDate", "s1"."ContentLicense", "Votes"."Id" AS "Id0", "Votes"."PostId", "Votes"."VoteTypeId", "Votes"."UserId", "Votes"."CreationDate" AS "CreationDate0", "Votes"."BountyAmount"
FROM "STACK"."Votes"
RIGHT JOIN "s1" ON "Votes"."PostId" = "s1"."Id") AS "t1" ON "Comments"."PostId" = "t1"."Id") AS "t2"
LEFT JOIN "STACK"."Badges" ON "t2"."OwnerUserId" = "Badges"."UserId"
GROUP BY "t2"."Id", "t2"."Title", "t2"."PostTypeId", "t2"."CreationDate") AS "t5" ON "Users"."Id" = "t5"."POSTID"
ORDER BY "t5"."CREATIONDATE" DESC NULLS FIRST) AS "t7"