SELECT COALESCE("POSTID", "POSTID") AS "POSTID", "TITLE", "CREATIONDATE", "VIEWCOUNT", "COMMENTCOUNT", "VOTECOUNT", "BADGECOUNT", "OWNERDISPLAYNAME", "OWNERREPUTATION", "LASTACTIVITYDATE"
FROM (SELECT ANY_VALUE("Id") AS "POSTID", "Title" AS "TITLE", "CreationDate" AS "CREATIONDATE", "ViewCount" AS "VIEWCOUNT", COUNT(DISTINCT "Id1") AS "COMMENTCOUNT", COUNT(DISTINCT "Id2") AS "VOTECOUNT", COUNT(DISTINCT "Id3") AS "BADGECOUNT", ANY_VALUE("DisplayName") AS "OWNERDISPLAYNAME", ANY_VALUE("Reputation") AS "OWNERREPUTATION", "LastActivityDate" AS "LASTACTIVITYDATE"
FROM "s1"
GROUP BY "Id", "CreationDate", "ViewCount", "LastActivityDate", "Title", "Reputation", "DisplayName"
ORDER BY "ViewCount" DESC NULLS FIRST) AS "t6"