SELECT COALESCE("t1"."EXPR$0", "t1"."EXPR$0") AS "FD_COL_0", "t10"."USERID", "t10"."DISPLAYNAME", "t10"."BADGECOUNT", "t10"."POSTCOUNT", "t10"."QUESTIONCOUNT", "t10"."ANSWERCOUNT", "t10"."TOTALSCORE", "t10"."AVERAGEVIEWS", "t10"."RECENTACTIVITYCOUNT"
FROM (SELECT AVG("TOTALSCORE") AS "EXPR$0"
FROM (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "POSTCOUNT", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "QUESTIONCOUNT", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "ANSWERCOUNT", SUM("Score") AS "TOTALSCORE", AVG("ViewCount") AS "AVERAGEVIEWS"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t0") AS "t1",
(SELECT "t5"."Id" AS "USERID", "t5"."DisplayName" AS "DISPLAYNAME", CASE WHEN "t5"."BADGECOUNT" IS NOT NULL THEN CAST("t5"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t7"."POSTCOUNT" IS NOT NULL THEN CAST("t7"."POSTCOUNT" AS BIGINT) ELSE 0 END AS "POSTCOUNT", CASE WHEN "t7"."QUESTIONCOUNT" IS NOT NULL THEN CAST("t7"."QUESTIONCOUNT" AS BIGINT) ELSE 0 END AS "QUESTIONCOUNT", CASE WHEN "t7"."ANSWERCOUNT" IS NOT NULL THEN CAST("t7"."ANSWERCOUNT" AS BIGINT) ELSE 0 END AS "ANSWERCOUNT", CASE WHEN "t7"."TOTALSCORE" IS NOT NULL THEN CAST("t7"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE", CASE WHEN "t7"."AVERAGEVIEWS" IS NOT NULL THEN CAST("t7"."AVERAGEVIEWS" AS INTEGER) ELSE 0 END AS "AVERAGEVIEWS", CASE WHEN "t9"."RECENTACTIVITYCOUNT" IS NOT NULL THEN CAST("t9"."RECENTACTIVITYCOUNT" AS BIGINT) ELSE 0 END AS "RECENTACTIVITYCOUNT"
FROM (SELECT *
FROM "STACK"."Users"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Badges"."UserId" = "Users0"."Id"
GROUP BY "Users0"."Id", "Users0"."DisplayName") AS "t4" ON "Users"."Id" = "t4"."USERID"
WHERE CASE WHEN "t4"."BADGECOUNT" IS NOT NULL THEN CAST("t4"."BADGECOUNT" AS BIGINT) ELSE 0 END > 0) AS "t5"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "POSTCOUNT", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "QUESTIONCOUNT", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "ANSWERCOUNT", SUM("Score") AS "TOTALSCORE", AVG("ViewCount") AS "AVERAGEVIEWS"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t7" ON "t5"."Id" = "t7"."OWNERUSERID"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(CASE WHEN "LastActivityDate" >= (TIMESTAMP '2024-10-01 12:34:56' - INTERVAL '30' DAY) THEN 1 ELSE NULL END) AS "RECENTACTIVITYCOUNT"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t9" ON "t5"."Id" = "t9"."OWNERUSERID") AS "t10"
WHERE "t10"."TOTALSCORE" >= "t1"."EXPR$0" OR "t10"."RECENTACTIVITYCOUNT" > 5