SELECT COALESCE("DISPLAYNAME", "DISPLAYNAME") AS "DISPLAYNAME", "ID", "REPUTATION", "TOTALPOSTS", "QUESTIONSCOUNT", "ANSWERSCOUNT", "GOLDBADGES", "SILVERBADGES", "BRONZEBADGES", "QUESTIONRATIO", "ANSWERRATIO", "RECENTVOTECOUNT", "REPUTATION0"
FROM (SELECT "DisplayName" AS "DISPLAYNAME", "Id" AS "ID", "Reputation" AS "REPUTATION", "TOTALPOSTS", "QUESTIONSCOUNT", "ANSWERSCOUNT", "GOLDBADGES", "SILVERBADGES", "BRONZEBADGES", "QUESTIONRATIO", "ANSWERRATIO", "FD_COL_23" AS "RECENTVOTECOUNT", "REPUTATION_" AS "REPUTATION0"
FROM "s1"
ORDER BY "REPUTATION_" DESC NULLS FIRST, "DisplayName"
