SELECT COALESCE("USERID", "USERID") AS "USERID", "DISPLAYNAME", "POSITIVECONTRIBUTIONS", "NEGATIVECONTRIBUTIONS", "CONTRIBUTINGPOSTS", "POSTTITLES", "CONTRIBUTIONTYPE"
FROM (SELECT "USERID", "DisplayName" AS "DISPLAYNAME", "POSITIVECONTRIBUTIONS", "NEGATIVECONTRIBUTIONS", "CONTRIBUTINGPOSTS", "POSTTITLES", CASE WHEN "POSITIVECONTRIBUTIONS" > "NEGATIVECONTRIBUTIONS" THEN 'Overall Positive Contributor' WHEN "NEGATIVECONTRIBUTIONS" > "POSITIVECONTRIBUTIONS" THEN 'Overall Negative Contributor' ELSE 'Neutral Contributor         ' END AS "CONTRIBUTIONTYPE"
FROM "s1"
ORDER BY "POSITIVECONTRIBUTIONS" DESC NULLS FIRST, "NEGATIVECONTRIBUTIONS") AS "t15"