SELECT COALESCE("Users"."Id", "Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", "t3"."BADGECOUNT", "t3"."GOLDBADGES", "t3"."SILVERBADGES", "t3"."BRONZEBADGES", "t0"."TOTALPOSTS", "t0"."QUESTIONS", "t0"."ANSWERS", "t0"."TAGWIKIS", "t0"."TOTALVIEWS", "t0"."TOTALSCORE", CASE WHEN "t0"."TOTALPOSTS" > 0 THEN "t0"."TOTALSCORE" / CAST("t0"."TOTALPOSTS" AS FLOAT) ELSE 0E0 END AS "AVGSCOREPERPOST"
FROM (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(*) AS "TOTALPOSTS", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "QUESTIONS", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "ANSWERS", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) IN (4, 5) THEN 1 ELSE NULL END) AS "TAGWIKIS", SUM("ViewCount") AS "TOTALVIEWS", SUM("Score") AS "TOTALSCORE"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t0"
INNER JOIN ("STACK"."Users" INNER JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Badges"."UserId" = "Users0"."Id"
GROUP BY "Users0"."Id", "Users0"."DisplayName") AS "t3" ON "Users"."Id" = "t3"."USERID") ON "t0"."OWNERUSERID" = "t3"."USERID"