SELECT COALESCE("Tags"."TagName", "Tags"."TagName") AS "TAGNAME", "Posts"."Id" AS "Id0", CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 1 THEN 1 ELSE 0 END AS "FD_COL_2", CASE WHEN CAST("Posts"."PostTypeId" AS INTEGER) = 2 THEN 1 ELSE 0 END AS "FD_COL_3", "Posts"."ViewCount", "Posts"."Score", "Posts"."CreationDate"
FROM "STACK"."Posts"
RIGHT JOIN "STACK"."Tags" ON "Posts"."Tags" LIKE '%' || "Tags"."TagName" || '%'