SELECT COALESCE("t11"."POSTID", "t11"."POSTID") AS "POSTID", "t11"."TITLE", "t11"."CREATIONDATE", "t11"."COMMENTCOUNT", "t11"."TOTALBOUNTY", "t11"."TAGS", "t11"."DISPLAYNAME", "t11"."BADGECOUNT", "t11"."TOTALREPUTATION", "t11"."HISTORYCOUNT", "t11"."LASTHISTORYDATE"
FROM (SELECT "t9"."POSTID", "t9"."TITLE", "t9"."CREATIONDATE", "t9"."COMMENTCOUNT", "t9"."TOTALBOUNTY", "t9"."TAGS", "t7"."DISPLAYNAME", "t7"."BADGECOUNT", "t7"."TOTALREPUTATION", "t5"."HISTORYCOUNT", "t5"."LASTHISTORYDATE"
FROM (SELECT ANY_VALUE("Posts0"."Id") AS "POSTID", COUNT("PostHistory"."Id") AS "HISTORYCOUNT", MAX("PostHistory"."CreationDate") AS "LASTHISTORYDATE"
FROM "STACK"."PostHistory"
RIGHT JOIN "STACK"."Posts" AS "Posts0" ON "PostHistory"."PostId" = "Posts0"."Id"
GROUP BY "Posts0"."Id") AS "t5"
INNER JOIN ("STACK"."Users" INNER JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT(DISTINCT "Badges"."Id") AS "BADGECOUNT", SUM("Users0"."Reputation") AS "TOTALREPUTATION"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Badges"."UserId" = "Users0"."Id"
GROUP BY "Users0"."Id", "Users0"."DisplayName") AS "t7" ON "Users"."Id" = "t7"."USERID" INNER JOIN (SELECT ANY_VALUE("Id") AS "POSTID", "TITLE", "CREATIONDATE", COUNT(DISTINCT "Id0") AS "COMMENTCOUNT", CASE WHEN SUM("BountyAmount") IS NOT NULL THEN CAST(SUM("BountyAmount") AS INTEGER) ELSE 0 END AS "TOTALBOUNTY", LISTAGG(DISTINCT "TagName", "FD_COL_6") AS "TAGS"
FROM "s1"
GROUP BY "Id", "TITLE", "CREATIONDATE") AS "t9" ON "Users"."Id" = "t9"."POSTID") ON "t5"."POSTID" = "t9"."POSTID"
ORDER BY "t9"."COMMENTCOUNT" DESC NULLS FIRST, "t7"."TOTALREPUTATION" DESC NULLS FIRST) AS "t11"