SELECT COALESCE("t2"."DisplayName", "t2"."DisplayName") AS "DISPLAYNAME", CASE WHEN "t2"."BADGECOUNT" IS NOT NULL THEN CAST("t2"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t4"."QUESTIONCOUNT" IS NOT NULL THEN CAST("t4"."QUESTIONCOUNT" AS BIGINT) ELSE 0 END AS "QUESTIONCOUNT", CASE WHEN "t4"."ANSWERCOUNT" IS NOT NULL THEN CAST("t4"."ANSWERCOUNT" AS BIGINT) ELSE 0 END AS "ANSWERCOUNT", CASE WHEN "t4"."TOTALSCORE" IS NOT NULL THEN CAST("t4"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE", CASE WHEN "t4"."AVGVIEWCOUNT" IS NOT NULL THEN CAST("t4"."AVGVIEWCOUNT" AS INTEGER) ELSE 0 END AS "AVGVIEWCOUNT", CONCAT('Gold: ', CAST(CASE WHEN "t2"."GOLDBADGES" IS NOT NULL THEN CAST("t2"."GOLDBADGES" AS INTEGER) ELSE 0 END AS VARCHAR CHARACTER SET "ISO-8859-1"), ', Silver: ', CAST(CASE WHEN "t2"."SILVERBADGES" IS NOT NULL THEN CAST("t2"."SILVERBADGES" AS INTEGER) ELSE 0 END AS VARCHAR CHARACTER SET "ISO-8859-1"), ', Bronze: ', CAST(CASE WHEN "t2"."BRONZEBADGES" IS NOT NULL THEN CAST("t2"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS VARCHAR CHARACTER SET "ISO-8859-1")) AS "BADGESUMMARY"
FROM (SELECT *
FROM "STACK"."Users"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Badges"."UserId" = "Users0"."Id"
GROUP BY "Users0"."Id", "Users0"."DisplayName") AS "t1" ON "Users"."Id" = "t1"."USERID"
WHERE CASE WHEN "t1"."BADGECOUNT" IS NOT NULL THEN CAST("t1"."BADGECOUNT" AS BIGINT) ELSE 0 END > 0) AS "t2"
LEFT JOIN (SELECT "OwnerUserId" AS "OWNERUSERID", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 1 THEN 1 ELSE NULL END) AS "QUESTIONCOUNT", COUNT(CASE WHEN CAST("PostTypeId" AS INTEGER) = 2 THEN 1 ELSE NULL END) AS "ANSWERCOUNT", SUM("Score") AS "TOTALSCORE", AVG("ViewCount") AS "AVGVIEWCOUNT", COUNT(DISTINCT "Tags") AS "UNIQUETAGCOUNT"
FROM "STACK"."Posts"
GROUP BY "OwnerUserId") AS "t4" ON "t2"."Id" = "t4"."OWNERUSERID"