SELECT COALESCE("Users"."Id", "Users"."Id") AS "USERID", "Users"."DisplayName" AS "DISPLAYNAME", CASE WHEN "t1"."BADGECOUNT" IS NOT NULL THEN CAST("t1"."BADGECOUNT" AS BIGINT) ELSE 0 END AS "BADGECOUNT", CASE WHEN "t1"."GOLDBADGES" IS NOT NULL THEN CAST("t1"."GOLDBADGES" AS INTEGER) ELSE 0 END AS "GOLDBADGES", CASE WHEN "t1"."SILVERBADGES" IS NOT NULL THEN CAST("t1"."SILVERBADGES" AS INTEGER) ELSE 0 END AS "SILVERBADGES", CASE WHEN "t1"."BRONZEBADGES" IS NOT NULL THEN CAST("t1"."BRONZEBADGES" AS INTEGER) ELSE 0 END AS "BRONZEBADGES", CASE WHEN "t3"."TOTALPOSTS" IS NOT NULL THEN CAST("t3"."TOTALPOSTS" AS BIGINT) ELSE 0 END AS "TOTALPOSTS", CASE WHEN "t3"."TOTALCOMMENTS" IS NOT NULL THEN CAST("t3"."TOTALCOMMENTS" AS BIGINT) ELSE 0 END AS "TOTALCOMMENTS", CASE WHEN "t3"."TOTALVOTES" IS NOT NULL THEN CAST("t3"."TOTALVOTES" AS BIGINT) ELSE 0 END AS "TOTALVOTES", CASE WHEN "t3"."TOTALVIEWS" IS NOT NULL THEN CAST("t3"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS "TOTALVIEWS", CASE WHEN "t3"."TOTALSCORE" IS NOT NULL THEN CAST("t3"."TOTALSCORE" AS INTEGER) ELSE 0 END AS "TOTALSCORE", CAST(CASE WHEN "t3"."TOTALSCORE" IS NOT NULL THEN CAST("t3"."TOTALSCORE" AS INTEGER) ELSE 0 END AS DECIMAL(12, 1)) / CASE WHEN CAST("t3"."TOTALPOSTS" AS BIGINT) = 0 OR "t3"."TOTALPOSTS" IS NULL THEN NULL ELSE CASE WHEN "t3"."TOTALPOSTS" IS NOT NULL THEN CAST("t3"."TOTALPOSTS" AS BIGINT) ELSE 0 END END AS "SCOREPERPOST", CAST(CASE WHEN "t3"."TOTALVIEWS" IS NOT NULL THEN CAST("t3"."TOTALVIEWS" AS INTEGER) ELSE 0 END AS DECIMAL(12, 1)) / CASE WHEN CAST("t3"."TOTALPOSTS" AS BIGINT) = 0 OR "t3"."TOTALPOSTS" IS NULL THEN NULL ELSE CASE WHEN "t3"."TOTALPOSTS" IS NOT NULL THEN CAST("t3"."TOTALPOSTS" AS BIGINT) ELSE 0 END END AS "VIEWSPERPOST"
FROM "STACK"."Users"
LEFT JOIN (SELECT ANY_VALUE("Users0"."Id") AS "USERID", "Users0"."DisplayName" AS "DISPLAYNAME", COUNT("Badges"."Id") AS "BADGECOUNT", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 1 THEN 1 ELSE 0 END) AS "GOLDBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 2 THEN 1 ELSE 0 END) AS "SILVERBADGES", SUM(CASE WHEN CAST("Badges"."Class" AS INTEGER) = 3 THEN 1 ELSE 0 END) AS "BRONZEBADGES"
FROM "STACK"."Badges"
RIGHT JOIN "STACK"."Users" AS "Users0" ON "Badges"."UserId" = "Users0"."Id"
GROUP BY "Users0"."Id", "Users0"."DisplayName") AS "t1" ON "Users"."Id" = "t1"."USERID"
LEFT JOIN (SELECT "t2"."OwnerUserId", COUNT(*) AS "TOTALPOSTS", COUNT("t2"."Id0") AS "TOTALCOMMENTS", COUNT(DISTINCT "Votes"."Id") AS "TOTALVOTES", SUM("t2"."ViewCount") AS "TOTALVIEWS", SUM("t2"."Score") AS "TOTALSCORE"
FROM "STACK"."Votes"
RIGHT JOIN (SELECT "Posts"."Id", "Posts"."PostTypeId", "Posts"."AcceptedAnswerId", "Posts"."ParentId", "Posts"."CreationDate", "Posts"."Score", "Posts"."ViewCount", "Posts"."Body", "Posts"."OwnerUserId", "Posts"."OwnerDisplayName", "Posts"."LastEditorUserId", "Posts"."LastEditorDisplayName", "Posts"."LastEditDate", "Posts"."LastActivityDate", "Posts"."Title", "Posts"."Tags", "Posts"."AnswerCount", "Posts"."CommentCount", "Posts"."FavoriteCount", "Posts"."ClosedDate", "Posts"."CommunityOwnedDate", "Posts"."ContentLicense", "Comments"."Id" AS "Id0", "Comments"."PostId", "Comments"."Score" AS "Score0", "Comments"."Text", "Comments"."CreationDate" AS "CreationDate0", "Comments"."UserDisplayName", "Comments"."UserId", "Comments"."ContentLicense" AS "ContentLicense0"
FROM "STACK"."Comments"
RIGHT JOIN "STACK"."Posts" ON "Comments"."PostId" = "Posts"."Id") AS "t2" ON "Votes"."PostId" = "t2"."Id"
GROUP BY "t2"."OwnerUserId") AS "t3" ON "Users"."Id" = "t3"."OwnerUserId"